#define wmod(p, w) mod(p, w) - w/2.
#define rot(a) mat2(cos(a + vec4(0, 11, 33, 0)))
#define t      iTime
#define PI     3.1415

float map(vec3 p) {
    float s = 0., d, l;

    p.zy *= rot(sin(t * 1.7) * .2);
    p.xy = wmod(p.xy, vec2(5, 3) / 4.);

    s = max(
            length(p.xy) - .15,   // cyl 
            abs(p.z) - 1.);     // size

    // glass
    return abs(s) - .005;
}


void mainImage(out vec4 o, vec2 u) {
    vec3 R = iResolution, q;

    float d, s, f, i, j,
          e = 1e-2;

    for(o *= 0.; i++ < 44. ; d += max(s, e / 5.)) {
        q = d * (vec3(u, R.y) - R/2.) / R.y - vec3(0, 0, 3.),  // p + d*D

        q.xz *= rot(cos(t) * .1);
        q.xy *= rot(sin(t) * .1);

        s = map(q);

        for(j = 4.; j < 16.; j += j) 
            s += .01 * abs(dot(sin(q *= j * 2.), q/q)) / j;

        s = abs(s) - .002,
        f = 1. - s / e, 

        f > -.5
            ? o += f / d * i / 300., f
            : o.g += .004;
    }
}
