void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

   float time=cos(iTime)*0.1+sin(uv.x*3.0+iTime)*0.25;

    if(uv.y<1.0/1.6+time)
    fragColor = vec4(148.0,0.0,211.0,0.0)/255.0;
    if(uv.y<1.0/1.80+time)
    fragColor = vec4(75.0,0.0,130.0,0.0)/255.0;
    if(uv.y<1.0/2.15+time)
    fragColor = vec4(0.0,0.0,255.0,0.0)/255.0;
    if(uv.y<1.0/2.5+time)
    fragColor = vec4(0.0,255.0,0.0,0.0)/255.0;
   	if(uv.y<1.0/3.0+time)
    fragColor = vec4(255.0,255.0,0.0,0.0)/255.0;
    if(uv.y<1.0/4.0+time)
    fragColor = vec4(255.0,127.0,0.0,0.0)/255.0;
    if(uv.y<1.0/6.0+time)
    fragColor = vec4(255.0,0.0,0.0,0.0)/255.0;
    if(uv.y<1.0/9.5+time)
    fragColor = vec4(1.0,1.0,1.0,1.0);
}
