void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord/iResolution.xy - 0.5) * 2.0;
    float aspect = iResolution.x / iResolution.y;
    uv.x *= aspect;

    fragColor.rg = uv;
    fragColor.b = 0.0;

    float thickness = iMouse.x / iResolution.x;
    thickness = 1.0;
    float fade = 0.00005;

    float distance = 1.0 - length(uv);
    vec3 col = vec3(smoothstep(0.0, fade, distance));
    col *= vec3(smoothstep(thickness, thickness - fade, distance));

    fragColor.rgb = vec3(col) * vec3(0.8, 0.3, 0.3);
    if(any(greaterThan(uv, vec2(1.0))) || any(lessThan(uv, vec2(-1.0))))
    {
        //fragColor.rgb = vec3(0.0);
    }
}
