void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.y;

    float zoom = 2.;
    float zoom_position = iTime * .5;

    uv /= pow(zoom, step(1.0, zoom_position) + fract(zoom_position));


    for(int i=0; i<9; i++) {
        uv *= 2.0;

        uv *= 1.0 - step(2.0 - floor(uv.y), uv.x);

        if(uv.y >= 1.0) {
            uv.y = 1.0 - uv.y;
        }

        if(uv.x >= 1.0) {
            uv.x = 1.0 - uv.x;
        }

        uv = fract(uv);
    }


    fragColor = vec4(uv, 0.0, 1.0);

}
