float gridNoise(vec2 uv, float time, float frequency) {
    vec2 gridPos = floor(uv * frequency);
    vec2 gridOffset = fract(uv * frequency) - 0.5;
    float t = sin(time) * 0.5 + 0.5;
    float color = mix(0.0, 1.0, t);
    float gridMod = mod(gridPos.x + gridPos.y, 2.0);

    return mix(color, 1.0 - color, gridMod);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord / iResolution.xx;

	float f = gridNoise( 1.0*uv, iTime*2.0, 4.0 );

	fragColor = vec4( f, f, f, 1.0 );
}
