void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //归一化坐标
    vec2 uv =  fragCoord/iResolution.xy;
    uv.x= uv.x*2.0-1.0;
    vec2 Coord = vec2(0.0);

    if(uv.x>-.0)
    {
        // 菱形转换
         float add_v = mod(floor(uv.y*iResolution.y),2.0)/iResolution.x;
         vec2 quincunxCoord = vec2(uv.x + add_v, uv.y);
         Coord = quincunxCoord;
    }else
    {
        // 正交像素
         Coord = uv;

    }
    fragColor = texture(iChannel0,Coord);

}
