const int n = 3; 
vec3 colAr[n] = vec3[n](vec3(1.0), vec3(0.0), vec3(1.0, 0.8, 0.0)); 
vec3 BGcol = vec3(0.2); 
float zAr[n] = float[n](1.0, 3.0, 4.0); 

bool inCircle(in vec2 uv,in vec2 move_of_center,in float r){ 
float d =length( uv - move_of_center - vec2(.5,.5) )-r;
  if (d<0.0) return true; 
  return false;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ){
  vec2 uv = fragCoord/iResolution.y; 
  vec2 m = vec2(cos(iTime)*0.5+0.3, sin(iTime)*0.5);
  vec2 m1 = vec2(0.3,0.0);
  vec2 m2 = vec2(0.2*cos(2.*iTime)+0.3,0.0);
  vec3 col=BGcol;
  if (inCircle(uv,m2,0.4)) col = colAr[1];
  else if (inCircle(uv,m,0.2)) col = colAr[0];
  else if (inCircle(uv,m1,0.4)) col = colAr[2];


  fragColor = vec4(col,1.0);
}
