    SOFTWARE.*/
	vec3 p3 = fract(vec3(p) * vec3(.1031, .1030, .0973));
	p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.xx+p3.yz)*p3.zy);

}




void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 uv0 = uv;
    float a = iResolution.x/iResolution.y;
    uv -= 0.5;
    uv.y /= a;

    if (iTime < 0.1) {
        fragColor = vec4(0., 0., 0., 1.);
        return;
    }

    //vec2 pos = hash21(iTime * 100.) - 0.5;
    vec2 pos = vec2(cos(iTime * 3.), sin(iTime * 4.)) / 4.;

    vec4 col = texture(iChannel0, uv0 + 0.005);

    vec4 txl = vec4(0.);
    for (int i = -3; i <= 3; i++)
    for (int j = -3; j <= 3; j++) {
        txl += texelFetch(iChannel0, ivec2(fragCoord) + ivec2(i + 1, j + 2), 0) / 49.;
    }
    col = txl * 1.005;

    if (abs(length(uv - pos)) < 0.01)
        col = vec4(pos + 0.5, 1., 1.);

    fragColor = col;
}
