void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5; // center
    uv.x *= iResolution.x / iResolution.y; // scale to aspect

    // idk
    float spiralTime = mod(length(fragCoord/iResolution.xy) + iTime, 16.0);
    float spiralRadius = 2.25 * spiralTime;
    vec2 spiralDirection = vec2(cos(spiralTime), sin(spiralTime));
    uv += spiralDirection * spiralRadius * 0.025;

    // grayscale from the uv stuff above
    float value = min(5.5, length(uv));
    vec3 col = vec3(min(0.75, value));

    //colorize
    col.xy *= 0.5;
    col.yz *= 0.7;

    if (value < 0.35) { col.xyz *= vec3(0.05, 0.05, mod(value, 0.15) * 9.0); }
    else if (value > 0.65) { col.xyz -= vec3(0.35, mod(value, 0.25) * 2.0, 0.68); }
    else if (value < 0.95) { col.xyz *= vec3(mod(value, 0.15) * 12.0, 0.85, 0.68); }
    else { col.xyz -= vec3(mod(value, 0.75) * 2.6, mod(value, 0.75) * 3.6, mod(value, 0.35) * 5.6); }

    col.xz *= col.yy + 0.325;
    col.xy *= 1.55;
    col.yz *= 0.97;

    // Output to screen
    fragColor = vec4(col, 1.0);
}
