float hash12(vec2 p)
{
	vec3 p3 = fract(vec3(p.xyx) * 443.8975);
	p3 += dot(p3, p3.yzx + 19.19);
	return fract((p3.x + p3.y) * p3.z);
}

vec3 hash32(vec2 p)
{
	vec3 p3 = fract(vec3(p.xyx) * vec3(443.897, 441.423, 437.195));
	p3 += dot(p3, p3.yxz + 19.19);
	return fract((p3.xxy + p3.yzz)*p3.zyx);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float ratio = iResolution.x/iResolution.y;
    vec2 uv = (fragCoord/iResolution.xy);
    uv.x *= ratio;

    float time = iTime;

    float st = step(sin(time*3.14), 0.5)+1.0;

    vec2 i = floor(uv*10.0)/10.0;

    vec3 ic = clamp((hash32(i + floor(time))-0.5)*3.0 + 0.5, 0.0, 1.0);

    float rand = hash12(i + floor(time));

    float g = step(rand, 0.3);
    float g2 = step(rand, 0.2);
    float green = mix(g, g2, st);


    fragColor = vec4(vec3(green, green, green)*ic,1.0);
}
