float circles(vec2 uv, vec2 pos, const float freq, const float line_width)
{
    if (line_width <= 0.)
        return 1e10;

    return abs(mod(length(uv - pos), freq) - .5 * freq) - line_width;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2. * fragCoord - iResolution.xy) / iResolution.y;

    float tex = 1. - texture(iChannel0, fragCoord / iResolution.xy).r - .1;

    vec3 col = vec3(1.);
    col = mix(col, vec3(.2, .4, 0), smoothstep(1.5, -1.5, circles(uv, vec2(.4, .8), .04, floor(0.5 + 4. * tex) / 288.) * iResolution.y));
    col = mix(col, vec3(.1, .2, 0), smoothstep(1.5, -1.5, circles(uv, vec2(-1.2, -.3), .04, floor(.1 + 5. * tex * tex) / 288.) * iResolution.y));
    col = mix(col, vec3(0, .1, 0), smoothstep(1.5, -1.5, circles(uv, vec2(1.3, -.8), .04, floor(.1 + 2. * tex) / 288.) * iResolution.y));

    col = pow(col, vec3(1. / 2.2));
    fragColor = vec4(col, 1.);
}
