#define LINE_WIDTH 0.7
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 colUv = uv;
    uv -= vec2(0.5, 0.5);
    uv.x = uv.x * iResolution.y / iResolution.x;

    float sdf = uv.y * 20. - sin(uv.x * 50.);
    float scalar = sdf < LINE_WIDTH/2. && sdf > -LINE_WIDTH/2. ? 1. : 0.;
    vec3 col = 0.5 + 0.5*cos(iTime+colUv.xyx+vec3(0,2,4));

    fragColor = vec4(col * scalar,1.0);
}
