#define H(u) vec3(u.x/.5, u.x + u.y/.577, u.x - u.y/.577)/2.  // hex coords
void mainImage( out vec4 C, vec2 U )
{
    vec2 R = iResolution.xy,
         m = (iMouse.xy - R/2.)/R.y,
         u = (U - R/2.)/R.y*3.;
    if (iMouse.z < 1.) m = cos(iTime*.314 - vec2(0, 1.57)); // circle with time
    vec3 h = abs(H(u)), c;
    h = (h*h + h.yzx*h.yzx + h.zxy*h.zxy - 1.)/h + H(m)*5.; // transform 1
    h /= h.yzx; // transform 2
    c = fwidth(h)/abs(h - round(h)); // grid
    c = min(c, vec3(1)) * min(abs(h), 1./abs(h)); // limit brightness & darken limits
    C = vec4(c + c.brg, 1);
}
