renderpass.code:
float FONT_SIZE=20.0;

vec2 codeToCharUv(int code){
    return (vec2(
        fract(
            float(code)/16.0
        )*16.0,
        15.0-floor(float(code)/16.0)
    )+vec2(0.25,0.0))/16.0;
}
// " .-:;r=?csedKm$QMW"
#define CHAR_LIST_LENGTH 18
int[CHAR_LIST_LENGTH] charList=int[](
0x20,0x2e,0x2d,0x3a,0x3b,0x72,0x3d,0x3f,0x63,0x73,0x65,0x64,0x4b,0x6d,0x24,0x51,0x4d,0x57
);

int remapToCharCode(float r){
  int index=clamp(int(r * float(CHAR_LIST_LENGTH)), 0, CHAR_LIST_LENGTH - 1);
  return charList[index];
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/vec2(FONT_SIZE*0.5,FONT_SIZE);
    // vec2 fuv=fract(uv);
    // if(fuv.x<=0.01 || fuv.y<=0.1){
    //     fragColor = vec4(vec3(0.0),1.0);
    //     return;
    // }
    vec2 uvBase = floor(fragCoord/vec2(FONT_SIZE*0.5,FONT_SIZE))*vec2(FONT_SIZE*0.5,FONT_SIZE);
    vec2 uvVideo = uvBase/iResolution.xy;

    //int c=0x35+int(sin(uvBase.x+iTime)*5.0);
    //int c=remapToCharCode(sin(uvBase.x+iTime)*0.5+0.5);
    vec3 videoColor=texture(iChannel1,uvVideo).rgb;
    int c=remapToCharCode(max(max(videoColor.r,videoColor.g),videoColor.b));
    vec2 charUv=codeToCharUv(c);
    vec2 uv2=(fract(uv)/16.0*vec2(0.5,1.0))+charUv;
    vec3 col=textureGrad(iChannel0,uv2,dFdx(uv2),dFdy(uv2)).rrr;

    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    // Output to screen
    fragColor = vec4(col,1.0);
}
