vec3 pal(float t){
    vec3 a = vec3(0.957, 0.415, 0.641);
    vec3 b = vec3(0.692, 0.515, 0.475);
    vec3 c = vec3(0.683, 1.171, 1.005);
    vec3 d = vec3(2.168, 2.388, 2.498);

    return a + b*cos(6.8318*(c*t+d));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy *2. -1.;

    uv.x *= iResolution.x/iResolution.y;
    vec2 uv0 = vec2(uv.x,uv.y);
    uv = fract(uv*8.+iTime);
    if(length(uv0)> 0.8 ){
        uv0 /= 2.*iTime;
        uv *= abs(sin(uv.x+iTime)+1.)+0.7;
    }else{
        //
    }
    vec3 col = pal(sign(uv.x-0.5)*sign(uv.y-0.5)+iTime/4.);
    fragColor = vec4(col,1.0);
}
