float shake_power = 0.1;

float shake_rate = .1;

float shake_speed = 50.0;

float shake_block_size = 30.5;

float shake_color_rate = 0.1;

float random(float seed){
    return fract( 543.2543 * sin( dot( vec2(seed,seed), vec2(3525.46, -54.3415) ) ) );
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float enable_shift = float( random( trunc(iTime * shake_speed) ) < shake_rate);

    vec2 uv = fragCoord/iResolution.xy;

    vec2 fixed_uv = uv;
    vec4 pixel_color = texture(iChannel1, uv);

    fixed_uv.x += (random((trunc(uv.y*shake_block_size) / shake_block_size) + iTime)-0.5)*shake_power*enable_shift;

    pixel_color.r = mix(
        pixel_color.r,
        texture(iChannel1, fixed_uv + vec2(shake_color_rate, 0.0), 0.0).r,
        enable_shift
        );

    pixel_color.b = mix(
        pixel_color.b,
        texture(iChannel1, fixed_uv + vec2( -shake_color_rate, 0.0), 0.0).b,
        enable_shift
        );


    // Output to screen
    fragColor = vec4(pixel_color);
}
