// ----------------------------------------------------------------------
float BeckmannDistribution( vec3 H, vec3 N, float m ) 
// ----------------------------------------------------------------------
{
    float D;
    float alpha = acos(max(dot(N,H), EPSILON));
    D = (exp(-(pow((tan(alpha)/m),2.0))))/max(pow(m,2.0)*pow(cos(alpha),4.0), EPSILON);

    #ifdef USE_INV_PI 
        D *= INV_PI;
    #endif

    return D;
}


// ----------------------------------------------------------------------
float SchlickFresnel ( float NdotH, float rho) //NdotH
// ----------------------------------------------------------------------
{
    float F;
    float n = refractive_index;
    float C = NdotH; //LdotH;
    float G = n * n + C * C - 1.0;

    //float n1 = refractive_index;
    //float n2 = 1.0; // refractive index of air
    //rho = 1.0;
    //F = rho + ((1.0 - rho) * pow((1.0 - NdotH), 5.0));
    F = 0.5 * pow(G - C, 2.0) * (1.0 + pow(C * (G + C) - 1.0, 2.0)/pow(C * (G + C) + 1.0, 2.0)) / pow(G + C, 2.0);

    return F; 
}

// ----------------------------------------------------------------------
float GeometricAttenuation( float NdotH, 
                            float NdotV, 
                            float NdotL, 
                            float HdotV )
// ----------------------------------------------------------------------
{  
    float G;

    G = min(1.0,min((2.0*NdotH*NdotV)/max(HdotV, EPSILON),(2.0*NdotH*NdotL)/max(HdotV, EPSILON)));

    return G; 
}

float integrand(float x) {
    return exp(-x) / x;
}

float riemann_sum(float lower_limit) {
    float num_intervals = 1000.0;
    float interval_width = 1.0;//(INFINITY - lower_limit) / num_intervals;
    float riemann_sum = 0.0;

    for (float i = 0.0; i < num_intervals; i++) {
        float xi = lower_limit + (i + 0.5) * interval_width;
        float riemann_sum_old = riemann_sum;
        riemann_sum += integrand(xi) * interval_width;

        if (riemann_sum - riemann_sum_old < EPSILON) 
        {
            break;
        }
    }

    return riemann_sum;
}

vec2 prospectModel()
{
    float n = refractive_index; //refractive index
    float C = riemann_sum(k);

    float theta = (1.0 - k) * exp(-k) + k * k * C;//(pow(k,-1.0) * exp(-k));
    float rho90 = (1.0 - tav90) + (tav90 * tav90 * theta * theta * (n*n - tav90))/( n*n*n*n - theta * theta * pow( n*n - tav90, 2.0));
    float tau90 = (tav90 * tav90 * theta * n * n ) /( n*n*n*n - theta * theta * pow( n*n - tav90, 2.0));
    float delta90 = sqrt(pow(tau90 * tau90 - rho90 * rho90 - 1.0, 2.0) - 4.0 * rho90 * rho90);
    float a90 = (1.0 + rho90 * rho90 - tau90 * tau90 + delta90)/(2.0 * rho90);
    float b90 = (1.0 + rho90 * rho90 - tau90 * tau90 + delta90)/(2.0 * tau90);
    float R90 =(pow(b90, layers) - pow(b90, -layers))/(a90 * pow(b90, layers) - pow(a90, -1.0) * pow(b90, layers));
    float T90 =(a90 - pow(a90, -1.0))/(a90 * pow(b90, layers) - pow(a90, -1.0) * pow(b90, layers));

    float x = tav59 / tav90;
    float y = x * (tav59 - 1.0) + 1.0 - tav59;

    float R = x * R90 + y;
    float T = x * T90;

    float diff_reflectance = kd * R ;
    float transmittance = kt * T * exp(-h);
    //return mix( diff_reflectance, transmittance, mixrateT);
    return vec2 (diff_reflectance, transmittance);
}


float KelemenSzirmayBRDF( vec3 L, vec3 V, vec3 N, float m, float rho ) 
// ----------------------------------------------------------------------
{
    // The result of your Cook-Torrance BRDF implementation
    float specularBRDF;
    // Numerator and Denominator of the main Cook-Torrance eq.
    float num, num1, num2, den, den1, den2; 
    // The 3 main fuctions of Cook-Torrance
    float D, G, F, rho1;

    vec3 H = normalize(L + V);
    //vec3 H = cooktorrence? normalize(L + V): (L + V) * (L + V);

    // Allow user selection by editing the #define and consts for 
    // the Beckmann distribution and the numerators of the 
    // main Cook-Torrance equation (refer to top of the shader code)

    D = BeckmannDistribution(H, N, m);
    G = GeometricAttenuation(max(dot(N, H), EPSILON),max(dot(N, V), EPSILON),max(dot(N, L), EPSILON),max(dot(H, V), EPSILON));
    if (cooktorrence){
        F = SchlickFresnel(max(dot(N, H), EPSILON), rho); 
    } else {
        F = SchlickFresnel(max(dot(N, H), EPSILON), rho); //*LdtoH, but NdotH works better
    }

    // Possible Combinations of 'D', 'G', and 'F' functions  
    // i.e., numerators of the main Cook-Torrance equation
    // Refer to the top of the shader's code.

    if (layers > 2.5)
    {
        rho1 = 0.0;
    } else {
        rho1 = ks * pow( 0.7, 1.0/oblateness);
    }

    num1 = rho1 * max(( D * F ), EPSILON );
    num2 = rho1 * max(( D * G * F ), EPSILON );

    H = (L + V);
    den1 = dot(H, H);
    den2 = max(dot(N, L), EPSILON) * max(dot(N, V), EPSILON);

    num = cooktorrence? num2 : num1;
    den = cooktorrence? den2 : den1;
    den = max( den, EPSILON ); // Avoid division by zero
    specularBRDF = num / den;

    #ifdef USE_INV_PI 
        specularBRDF *= INV_PI;
    #endif

    return specularBRDF;
}

