vec2 triangle_wave(vec2 a){
    vec2 a2 =
        vec2(1.,0.5)
    ,
    a1 = a+a2;
    return abs(fract((a1)*(a2.x+a2.y))-.5);
}

    float scale = 1.5;


void transform(inout vec2 uv, inout vec2 t2, inout vec3 col,inout float c1){
    uv.y -= 1./4.;

        uv = (uv+t2)/scale;

        uv = (fract(vec2(uv+vec2(.5,1.5))*scale)-.5)/scale;

        t2 = triangle_wave(uv+.5);
        uv =
            //t2-triangle_wave(uv.yx)+fract(t2/2.)

            //looks better with more iterations
            t2-triangle_wave(uv.yx)-fract(t2/2.)
        ;
}

vec3 fractal(vec2 uv){
    vec3 col = vec3(0.);
    vec2 t2 = vec2(0.);
    vec3 col1 = col;
    float c1=0.;
    for(int k = 0; k < 12; k++){

        vec2 uv_1 =
            uv
            //uv/2.
        ;
        vec2 t2_1 =
            t2
            //t2*2.
        ;

        transform(uv,t2,col,c1);
        transform(uv_1,t2_1,col,c1);

        c1 =
            //max(abs(uv.y-uv.x*sign(uv.x-uv.y))/2.,c1)
            max(abs(uv_1.y+uv_1.x)/2.,c1)
        ;
        c1 =
            max(1.-abs(2.*c1-1.),c1/4.)

            //more colorful
            //abs(2.*c1-1.)

            //Spooky!
            //max(abs(2.*c1-2.),c1/4.)/2.
        ;
        col.x =
            max(length(uv_1-t2_1-c1)/3.,col.x)

            //blue and white
            //c1

            //red and blue and white
            //max(1.-abs(2.*c1-1.),c1/4.)


            //more colorful
            //max(length(uv-t2-c1)/3.,1.-col.x)

            //blue and green
            //max(length(uv-c1)/3.,col.x)

            //purple
            //2. - max((abs(uv.y-uv.x))/3.,col.x)

            //black and white
            //col.x
        ;
        col =
            abs(col-(1.-(c1*col.x)));
        col1 =
            abs(col1*c1-col-1.).yzx;
    }
    return col1;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    float t1 = 6.;

    vec2 uv = (fragCoord)/iResolution.y/t1/2.0;
    uv.xy += iTime/t1/12.;
    if(iMouse.z>.5)
    uv = uv.xy + iMouse.xy / iResolution.xy/t1;
    vec3 col1 = fractal(uv);
    fragColor = vec4(col1/2.,1.0);
}

//remix of https://www.shadertoy.com/view/mtSBzV

int collatz(int initial, int steps){
    //hailstone numbers
    //makes some interesting rhythms
    for(int i = 0; i < steps; i++){
        if(initial%2==0) initial /= 2;
        else initial = initial*3+1;
    }
    return initial;
}
float collatz(float initial, float steps){
    return float(collatz(int(initial),int(steps)));
}

float mod1(float a,float b){

    //a += collatz(mod(a/4.,b),8.);
    //a += collatz(mod(a/4.,8.),b);

    return
        mod(a,b)
    ;
}

float fmod(float a, float b){


    float
    t1 = mod1(floor(b/2.),2.),
    a1 =
        floor(a+t1),
    a2 = mod1(a1,3.),
    a3 =
        mod1(a1,4.)
    ;
    return
        mod1(a1-a2-a3,b)
    ;
}

float prime_rhythm(float time){
    return (1.+fmod(time/4.,2.))/(1.+fmod(time/2.,2.));
}

float fmod1(float a, float b){
    //a += collatz(a/8./8.,b);
    return fmod(a,b);
}

#define fract1(a) (1.-pow(fract(-a),2.))

vec2 synth_(vec2 a1,vec2 a,vec2 a2,vec2 a3,vec2 nb,vec2 a4){
    return log(a1+a*abs(a2-abs(.5-a3*vec2(fract1(nb.x*.998),fract1(nb.y)))*a4));
}
#define synth(a1,a,a2,a3,nb,a4) synth_(vec2(a1),vec2(a),vec2(a2),vec2(a3),vec2(nb),vec2(a4))

vec2 mainSound1(int samp, float time,float scale,float duration){

  float s1 =
      8.,
  tempo1 =
      prime_rhythm(time)*prime_rhythm(time/2.)*2.
  ,t = s1*time/4./tempo1,
  t4 = t/prime_rhythm(time+11.)/(1.+fmod(t/8.,2.)),
  t1 =
       fmod(t4,2.)
  ,
  t5 = t*2./prime_rhythm(time+7.),
  t3 =
      (1.+fmod(t5,2.))
  ,
  t6 = time*8./t3,
  m3 =
      floor(fmod1(t6,s1/2.)/2.),
  m2 =
      1. + fmod1(t/t3/2.,s1/2.)
  ,note=
      (m3+m2-t1*4.)/7.
  ;
  vec2 a=
      -vec2((log(1./64.+fract1(t/2./prime_rhythm(time+14.))/2./duration)),(log(1./64.+fract1(t/prime_rhythm(time+7.))/4./duration)))
  ,
  nb =
      t*vec2(pow(2.,note+scale),pow(2.,note+scale))*tempo1
  ;
  return
      //oud
      a/16./2.*abs(
      synth(1./2.,a,0.,1.,nb*2.,1./2.)
      +.5*synth(1./8.,a,1./8.,1.,nb,1./2.))/2.
  ;

}



vec2 mainSound1(int samp, float time,float scale){
    return mainSound1(samp,time,scale,1.);
}

vec2 mainSound(int a,float b){
    return
        //sounds like sitar (using oud)

        abs(mainSound1(a,b,6.25,1./2.)-mainSound1(a,b,7.25)/2.)
    ;
}
