void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;

    vec3 red = vec3(1.0, 0.0, 0.0);
    vec3 green = vec3(0.0, 1.0, 0.0); 
    vec3 blue = vec3(0.0, 0.0, 2.0); 
    vec3 yellow = vec3(1.0, 1.0, 0.0); 


    vec3 up = mix(green, vec3(-0.5), 1.-uv.y);
    vec3 down = mix(red, vec3(-0.5), uv.y);
    vec3 left = mix(blue, vec3(-0.5), uv.x);
    vec3 right = mix(yellow, vec3(-0.5), 1.-uv.x);

    vec3 color = normalize(up+down+left+right);


    // Convert from linear to 3.0 gamma by Fabrice
    color = pow(color, vec3(1./3.0));

    fragColor = vec4(color, 1.0);
}
