void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    const float zoom = 0.125;

    fragCoord = zoom*((fragCoord-0.5*iResolution.xy)/(0.5*iResolution.xy))+vec2(0.5,0.5);
    fragColor = vec4(texture(iChannel0,fragCoord).rgb, 1.0); // original
    //fragColor = vec4(texture(iChannel1,fragCoord).rgb, 1.0); // cfa color
    fragColor = vec4(texture(iChannel2,fragCoord).rgb, 1.0); // debayered
    //fragColor = vec4(texture(iChannel3,fragCoord).rgb, 1.0); // blurred
    fragColor = vec4(abs(fragColor.rgb),1.0); // change debayer to subtract for difference view
}

#define num_samples 13

// HRAA
const vec2 sample_offset[13] = vec2[](
    vec2(-1.000, 1.000),
    vec2( 0.000, 1.000),
    vec2( 1.000, 1.000),

    vec2(-0.500, 0.500),
    vec2( 0.500, 0.500),

    vec2(-1.000, 0.000),
    vec2( 0.000, 0.000),
    vec2( 1.000, 0.000),

    vec2(-0.500,-0.500),
    vec2( 0.500,-0.500),

    vec2(-1.000,-1.000),
    vec2( 0.000,-1.000),
    vec2( 1.000,-1.000)
);

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    fragColor = vec4(0);

    fragCoord += vec2(cos(3.0*iTime),sin(3.0*iTime));

    for(int i = 0; i < num_samples; i++) {
    //for(int i = 0; i < 1; i++) {
        vec2 dithCoord = fragCoord+0.5*sample_offset[i];
        //vec2 dithCoord = fragCoord+2.0*(texelFetch(iChannel1,ivec2(i+1,iFrame%256),0).xy-0.5);

        vec2 uv = (dithCoord-0.5*iResolution.xy)/(0.5*16.0);

        float th = 0.03*cos(3.1*iTime);

        //uv *= 0.5;

        uv *= mat2(cos(th),-sin(th), sin(th), cos(th));

        float a = dot(uv,uv) <= 1.0 ? 1.0 : 0.0;

        fragColor += vec4(mix(texture(iChannel0,8.0*0.001953125*uv+0.5).rgb,vec3(1),a),1.0);

        fragColor += vec4(mix(texture(iChannel1,2.0*0.001953125*uv+0.5).rgb,vec3(1),a),1.0);
    }

    fragColor = vec4(fragColor.a != 0.0 ? fragColor.rgb/fragColor.a : fragColor.rgb, 1.0);

    //fragColor.rgb = log(fragColor.rgb+1.0);

    fragColor.rgb = clamp(fragColor.rgb, 0.0, 1.0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    fragColor = texelFetch(iChannel0, ivec2(fragCoord), 0);

    /*

    CFA Pattern:
    +---+---+
    | 0 | 1 |
    +---+---+
    | 2 | 3 |
    +---+---+

    */

    int cfa = (int(fragCoord.x)%2)+(2*(((int(iResolution.y)-1)-int(fragCoord.y))%2));

    fragColor.rgb *= 1.0-0.0*texture(iChannel1,(fragCoord+1024.0*texture(iChannel2,vec2(iFrame%256,iFrame/256)/256.0).xy)/1024.0).rgb;

    if(cfa == 0) fragColor.rgb *= vec3(1.0, 0.0, 0.0);
    if(cfa == 1) fragColor.rgb *= vec3(0.0, 1.0, 0.0);
    if(cfa == 2) fragColor.rgb *= vec3(0.0, 1.0, 0.0);
    if(cfa == 3) fragColor.rgb *= vec3(0.0, 0.0, 1.0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // standard linear method
    // sharpest but also subject to false color and zippering
    fragColor = vec4(
    0.250*texelFetchOffset(iChannel0,ivec2(fragCoord),0,ivec2(-1, 1)).r+
    0.500*texelFetchOffset(iChannel0,ivec2(fragCoord),0,ivec2( 0, 1)).r+
    0.250*texelFetchOffset(iChannel0,ivec2(fragCoord),0,ivec2( 1, 1)).r+
    0.500*texelFetchOffset(iChannel0,ivec2(fragCoord),0,ivec2(-1, 0)).r+
    1.000*texelFetchOffset(iChannel0,ivec2(fragCoord),0,ivec2( 0, 0)).r+
    0.500*texelFetchOffset(iChannel0,ivec2(fragCoord),0,ivec2( 1, 0)).r+
    0.250*texelFetchOffset(iChannel0,ivec2(fragCoord),0,ivec2(-1,-1)).r+
    0.500*texelFetchOffset(iChannel0,ivec2(fragCoord),0,ivec2( 0,-1)).r+
    0.250*texelFetchOffset(iChannel0,ivec2(fragCoord),0,ivec2( 1,-1)).r,

    0.250*texelFetchOffset(iChannel0,ivec2(fragCoord),0,ivec2( 0, 1)).g+
    0.250*texelFetchOffset(iChannel0,ivec2(fragCoord),0,ivec2(-1, 0)).g+
    1.000*texelFetchOffset(iChannel0,ivec2(fragCoord),0,ivec2( 0, 0)).g+
    0.250*texelFetchOffset(iChannel0,ivec2(fragCoord),0,ivec2( 1, 0)).g+
    0.250*texelFetchOffset(iChannel0,ivec2(fragCoord),0,ivec2( 0,-1)).g,

    0.250*texelFetchOffset(iChannel0,ivec2(fragCoord),0,ivec2(-1, 1)).b+
    0.500*texelFetchOffset(iChannel0,ivec2(fragCoord),0,ivec2( 0, 1)).b+
    0.250*texelFetchOffset(iChannel0,ivec2(fragCoord),0,ivec2( 1, 1)).b+
    0.500*texelFetchOffset(iChannel0,ivec2(fragCoord),0,ivec2(-1, 0)).b+
    1.000*texelFetchOffset(iChannel0,ivec2(fragCoord),0,ivec2( 0, 0)).b+
    0.500*texelFetchOffset(iChannel0,ivec2(fragCoord),0,ivec2( 1, 0)).b+
    0.250*texelFetchOffset(iChannel0,ivec2(fragCoord),0,ivec2(-1,-1)).b+
    0.500*texelFetchOffset(iChannel0,ivec2(fragCoord),0,ivec2( 0,-1)).b+
    0.250*texelFetchOffset(iChannel0,ivec2(fragCoord),0,ivec2( 1,-1)).b,

    1.0);

    //fragColor.rgb = fragColor.rrr;
    //fragColor.rgb = fragColor.ggg;
    //fragColor.rgb = fragColor.bbb;

    // mipmap method
    //fragColor.rgb = vec3(4.0,2.0,4.0)*texelFetch(iChannel0,ivec2(fragCoord)/2,1).rgb;
    //fragColor.rgb = mix(fragColor.rgb,vec3(4.0,2.0,4.0)*textureLod(iChannel0,fragCoord/iResolution.xy,1.0).rgb,0.5);

    // alternate linear method (smooths green)
    vec3 color = vec3(
    0.5*texelFetchOffset(iChannel0,ivec2(fragCoord),0,ivec2(-1, 1))+
    texelFetchOffset(iChannel0,ivec2(fragCoord),0,ivec2( 0, 1))+
    0.5*texelFetchOffset(iChannel0,ivec2(fragCoord),0,ivec2( 1, 1))+
    texelFetchOffset(iChannel0,ivec2(fragCoord),0,ivec2(-1, 0))+
    2.0*texelFetchOffset(iChannel0,ivec2(fragCoord),0,ivec2( 0, 0))+
    texelFetchOffset(iChannel0,ivec2(fragCoord),0,ivec2( 1, 0))+
    0.5*texelFetchOffset(iChannel0,ivec2(fragCoord),0,ivec2(-1,-1))+
    texelFetchOffset(iChannel0,ivec2(fragCoord),0,ivec2( 0,-1))+
    0.5*texelFetchOffset(iChannel0,ivec2(fragCoord),0,ivec2( 1,-1)));

    // blend alternate method
    //fragColor.rgb = mix(fragColor.rgb, vec3(0.5, 0.25, 0.5)*color, 0.5);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    fragColor = vec4(
    1.000*texelFetchOffset(iChannel0,ivec2(fragCoord),0,ivec2( 0, 1)).rgb+
    1.000*texelFetchOffset(iChannel0,ivec2(fragCoord),0,ivec2(-1, 0)).rgb+
    8.000*texelFetchOffset(iChannel0,ivec2(fragCoord),0,ivec2( 0, 0)).rgb+
    1.000*texelFetchOffset(iChannel0,ivec2(fragCoord),0,ivec2( 1, 0)).rgb+
    1.000*texelFetchOffset(iChannel0,ivec2(fragCoord),0,ivec2( 0,-1)).rgb,
    1.0);

    fragColor.rgb /= 12.0;
}
