void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    vec3 col2 = 0.25 + 0.5*sin(iTime+uv.xyx+vec3(0,2,4));

     // creating a cross sign

    vec2 size = vec2(0.1, 0.2);

     float yOffset = (1.0 - size.y) / 2.0;

    // Create the horizontal line of the cross
    bool horizontalLine = abs(uv.y - 0.5) < size.y / 2.0 ;

    // Create the vertical line of the cross
    bool verticalLine = abs(uv.x - 0.5) < size.x / 2.0 ;

    // Set the color of the cross
    vec3 color = vec3(1.0, 1.0, 1.0); // White color


     //check if the current fram is part of the plus sign

     if (verticalLine || horizontalLine) {
        fragColor = vec4(col, 1.0);
     } else {
        fragColor = vec4(col2, 1.0);
     }
}
