#define PI 3.1415926535897932384626433832795

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 pos = (uv.xy-0.5)*2.0;

    // Add a zoom effect
    float zoom = 1.0 + sin(iTime) * 0.99;
    pos *= zoom;

    // Calculate the radial pattern with additional complexity
    vec2 cir = ((pos.xy*pos.xy+sin(uv.x*18.0+iTime)/25.0*sin(uv.y*7.0+iTime*1.5)/1.0)+uv.x*sin(iTime)/16.0+uv.y*sin(iTime*1.2)/16.0);
    float circles = (sqrt(abs(cir.x+cir.y*0.5)*25.0)*5.0);

    // Add additional layers of color
    float r = 0.5 + 0.5 * cos(iTime + circles);
    float g = 0.5 + 0.5 * cos(iTime + circles + 2.0*PI/3.0);
    float b = 0.5 + 0.5 * cos(iTime + circles + 4.0*PI/3.0);

    fragColor = vec4(r, g, b, 1.0);
}
