const float PI = 3.1415926535897932384626433832795;
const float EN = 2.7182818284590452353602874713527;
const float PHI = (1.0+sqrt(5.0))/2.0;
const vec3 w = vec3(PHI*EN,PHI*PI,PHI*sqrt(2.))*4.;
const float nring = 2.;

vec3 palette( in float t)
{
    vec3 a = vec3(0.758, 0.928, 0.590);
    vec3 b = vec3(-0.478, 0.580, 0.949);
    vec3 c = vec3(1.098, -0.474, 1.005);
    vec3 d = vec3(-4.163, -5.315, -5.315);
    return a + b*cos( 6.28318*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord*2.0 - iResolution.xy) / iResolution.y;
    vec2 uv0 = uv;
    float d0 = length(uv0);

    vec3 finalColor = vec3(0.0);

    for (float i = 0.0; i < 3. ;i++){
        uv = fract(uv*PHI) - .5;

        float d = length(uv) * exp(-length(uv0));
        float r = abs(sin(2.0*PI*(d*nring + mod(iTime,w.r)/w.r) ));
        float g = abs(sin(2.0*PI*(d*nring + mod(iTime,w.g)/w.g) ));
        float b = abs(sin(2.0*PI*(d*nring + mod(iTime,w.b)/w.b) ));

        r = pow(.05 / r,1.7);
        g = pow(.05 / g,1.7);
        b = pow(.05 / b,1.7);

        float duv = d*uv.y+uv.x;
        float duv0 = d0*uv0.y+uv0.x;
        float df = pow(duv*duv + duv0*duv0,.5);
        df = d/d0;
        vec3 colr = palette(df + iTime/w.r + i*10.)*r;
        vec3 colg = palette(df + iTime/w.g + i*10.)*g;
        vec3 colb = palette(df + iTime/w.b + i*10.)*b;
        finalColor += (colr+colg+colb)/3.;
    }
    // Output to screen
    fragColor = vec4(finalColor,1.0);
}
