float circle(vec2 fc, vec2 pos, float r) {

    return 1.-step(1.0, distance(fc, pos)/r);
}

float rect(vec2 fc, vec2 c, vec2 dim) {
    if(fc.x - c.x > -0.5*dim.x && fc.x - c.x < 0.5*dim.x &&
       fc.y - c.y > -0.5*dim.y && fc.y - c.y < 0.5*dim.y) {
        return 1.0;
    }
    else {
        return 0.0;
    }
}

float logo(vec2 fc, vec2 posi, float s) {
  float clr; 
  vec2 pos = posi + vec2(-0.25*s, -0.5*s);
  clr = circle(fc, pos, s); 
  clr -= circle(fc, pos, 0.5*s);
  clr = min(clr+rect(fc, vec2(pos.x+0.75*s, pos.y+s), vec2(0.5*s,2.*s)), 1.0);
  clr = max(clr-rect(fc, vec2(pos.x+0.375*s, pos.y+s), vec2(0.25*s,2.*s)), 0.0);
  clr = min(clr+circle(fc, pos+vec2(1.5*s, 2.*s), s) - circle(fc, pos+vec2(1.5*s, 2.*s), 0.5*s), 1.0);
  clr = max(clr-min(rect(fc, pos+vec2(2.*s, 2.*s), vec2(s, 2.*s)) + rect(fc, pos+vec2(1.5*s, 2.5*s), vec2(2.*s, s)), 1.0), 0.0);
  return clr;
}
vec4 animatedLogo(vec2 fc, vec2 rs) {
    vec4[] palette = vec4[] (vec4(0,0,0,0), vec4(0.14,0.93,0.91, 1), vec4(0.95,0,0.29, 1), vec4(1,1,1,1));

    float logo1 = logo(fc, 0.5*rs + vec2(-1., 1.)*sin(6.*iTime)*rs.y*0.006, rs.y*0.1);
    float logo2 = logo(fc, 0.5*rs + vec2(1., -1.)*sin(6.*iTime)*rs.y*0.006, rs.y*0.1);
    int[] logoMix = int[] (int(logo1), int(logo2));
    vec4 clr = palette[2*logoMix[0] + logoMix[1]];
    return clr;
}

float heart(vec2 fc, vec2 pos, float s) {
    float clr;
    vec2 basisX = normalize(vec2(1,1));
    vec2 basisY = vec2(-basisX.y,basisX.x);
    vec2 pos_ = vec2(iTime, 0);
    clr = rect(vec2(dot(fc-pos, basisX),dot(fc-pos, basisY)), vec2(0.), s*vec2(1,1));
    clr = min(clr + circle(fc, pos+vec2(0.33)*s, 0.5*s) + circle(fc, pos+vec2(-0.33, 0.33)*s, 0.5*s), 1.);
    return clr;
}

float heartGrid(vec2 fc) {
    vec2 basisX = normalize(vec2(4,1));
    vec2 basisY = vec2(-basisX.y,basisX.x);
    vec2 fci = vec2(dot(fc, basisX), dot(fc, basisY));
    fci = mod(fci+vec2(40.*-iTime,0), 50.);

    float clr = heart(fci, vec2(20., 20.), 20.);
    return clr;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 fc = fragCoord;
    vec2 rs = iResolution.xy;
    vec4 canvas;
    vec4 tktk = animatedLogo(fc, rs);
    float circ = circle(fc, vec2(0.5*rs.x, 0.5*rs.y), rs.y*0.25);
    vec4 circLogo = vec4(1,1,1,1)*(1.-circ);
    circLogo = tktk*tktk.w + circLogo*(1.-tktk.w);
    vec4 clr = circLogo*circ + 0.2*vec4(1,1,1,1)*heartGrid(fc)*(1.-circ);

    fragColor = clr;
}
