//by Ingo Quilez
vec3 palette( in float t)
{
    //[[0.708 0.178 -0.052] [-2.280 0.731 1.329] [3.437 1.905 3.730] [0.098 -2.492 0.308]]
    vec3 a = vec3(0.708, 0.178, -0.052);
    vec3 b = vec3(-2.280, 0.731, 1.329);
    vec3 c = vec3(1.0, 1.0, 1.0);
    vec3 d = vec3(0.098, -2.492, 0.308);
    //vec3 d = vec3(-0.083, -2.558, -0.313);
    return a + b*cos( 6.28318*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;

    uv /= 3.0;

    vec3 c = 0.3/palette(length(uv)*0.5+0.1*iTime);
    c = abs(c);

    c = palette(length(uv)+iTime);

    c = palette(uv.x-uv.y);

    float d = uv.x-uv.y+iTime*0.05;
    d *= 6.2;
    d = sin(d * 10.);
    d = .5 / d;
    d = abs(d);
    c = palette(uv.x-uv.y + iTime*0.15 ) * d;

    fragColor.xyz = c;
}
