vec3 rotateY(vec3 p, float angle) {
    float s = sin(angle);
    float c = cos(angle);
    return vec3(p.x*c - p.z*s, p.y, p.z*c + p.x*s);
}

float map(vec3 p) {
    vec3 boxSize = vec3(0.3);
    vec3 boxDist = abs(p) - boxSize;
    return length(max(boxDist, 0.0)) + min(max(boxDist.x, max(boxDist.y, boxDist.z)), 0.0);
}

vec3 color(vec3 p) {
    return 0.5 + 0.5 * cos(iTime + 3.0 * p);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.y - 0.5;
    uv.x *= iResolution.x / iResolution.y;

    vec3 ro = vec3(0, 0, -3.0);
    vec3 rd = normalize(vec3(uv, 2.0));

    float t = 0.0;
    for (int i = 0; i < 64; i++) {
        vec3 p = ro + rd * t;
        float d = map(rotateY(p, iTime));
        if (d < 0.001) {
            fragColor = vec4(color(p), 1.0);
            return;
        }
        t += d;
        if (t > 4.0) {
            break;
        }
    }
    fragColor = vec4(vec3(0.0), 1.0);
}
