void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,
         U = ( 2.*u - R ) / R.y ;
    O = .5 + .5*cos(iTime+U.xyxx+vec4(0,2,4,0));

    U *= mat2(cos( tan(7.+iTime)/.3 +vec4(0,33,11,0)));
    U = fract(U*6.) - vec2(.8, .5) ;
    U = smoothstep( 18./R.y, 0., U );
    O *= U.x*U.y; 
}
