void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord;
    uv *= 1.0;    
    uv = vec2(uv.x/iResolution.x,uv.y/iResolution.y);
    uv = fract(uv);

    float d = length(uv-vec2(.5));
    fragColor = texture(iChannel2,(uv-vec2(.5))*(1.+sin(d*80.-iTime*10.)*0.1)+vec2(0.5));
}
