void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (2.0*fragCoord-iResolution.xy)/iResolution.y;
    vec2 m = (2.0*iMouse.xy-iResolution.xy)/iResolution.y;
    vec2 p = uv;
    float c1 = sdCircle(p, 0.4);
    float c2 = sdCircle(p-m, 0.15);
    float c3 = max(c1,c2);

    float result = smoothMin(c1,c2,5.0);
    result = smoothstep(0.0, 0.01, result);
    result = 1.0 - result;

    vec3 color1 = vec3(1.000,0.000,0.000);
    vec3 color2 = vec3(0.267,0.000,1.000);
    vec3 color3 = color1*color2;
    vec3 col = color3;

    col = mix(color1, col, smoothstep(0.0, 0.2, c1));
    col = mix(color2, col, smoothstep(0.0, 0.1, c2));
    col = mix(color3, col, smoothstep(0.0, 0.2, c3));
    col = mix(vec3(1.0), col, result);

    // Output to screen

    fragColor = vec4(col, 1.0);
}

float sdCircle( vec2 p, float r )
{
    return length(p) - r;
}

float smoothMax(float a, float b, float k){
    return log(exp(k*a)+exp(k*b))/k;
}

float smoothMin(float a, float b, float k){
    return -smoothMax(-a,-b,k);
}
