void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // 创建时间周期为3秒
    float timeCycle = mod(iTime, 6.0);
    vec3 randomColor;

    // 选择一个随机颜色（在每个周期内固定）
    if (timeCycle < 3.0) {
        randomColor = vec3(1.0, 0.0, 0.0); // 红色
    } else {
        randomColor = vec3(0.0, 1.0, 0.0); // 绿色
    }

    // 将坐标映射到1920x1080分辨率范围内
    vec2 uv = fragCoord.xy / iResolution.xy;
    uv *= vec2(1920.0, 1080.0);

    // 计算条纹
    float stripe;
    if (timeCycle < 3.0) {
        stripe = mod(uv.x + iTime * 200.0, 200.0); // 增加条纹宽度至200个像素
    } else {
        stripe = mod(uv.y + iTime * 200.0, 200.0); // 增加条纹宽度至200个像素
    }

    // 设置颜色
    vec3 color;
    if (stripe < 100.0) { // 宽度是200像素，所以中间点在100像素
        // 随机颜色的白条纹
        color = randomColor;
    } else {
        // 黑条纹
        color = vec3(0.0);
    }

    fragColor = vec4(color, 1.0);
}
