uint binary_gcd(uvec2 v) // https://www.shadertoy.com/view/mltyRM binary gcd
{
    if (v.x == 0u) return v.y;
    if (v.y == 0u) return v.x;
    uint k;
    for(k = 0u; ((v.x | v.y) & 1u) == 0u; ++k) v >>= 1u;
    while((v.x & 1u) == 0u) v.x >>= 1u;
    do
    {
        while((v.y & 1u) == 0u) v.y >>= 1u;
        if (v.x > v.y) v = v.yx;
        v.y -= v.x;
    }
    while (v.y != 0u);
    return v.x << k;
}

uint gcd(uvec2 v) // NOTE: modulo-based gcd is way more compact...
{
    while(v.y != 0u) v = uvec2(v.y, v.x % v.y);
    return v.x;
}

// https://www.shadertoy.com/view/WttXWX "Best" Integer Hash by FabriceNeyret2,
// implementing Chris Wellons https://nullprogram.com/blog/2018/07/31/
uint triple32(uint x)
{
    x ^= x >> 17;
    x *= 0xed5ad4bbU;
    x ^= x >> 11;
    x *= 0xac4c1b51U;
    x ^= x >> 15;
    x *= 0x31848babU;
    x ^= x >> 14;
    return x;
}

// Wellons lowbias32 from http://nullprogram.com/blog/2018/07/31
// perhaps the best ever devised for this op count, great distribution and cycle
uint lowbias32(uint x)
{
    x ^= x >> 16;
    x *= 0x7feb352du;
    x ^= x >> 15;
    x *= 0x846ca68bu;
    x ^= x >> 16;
    return x;
}

#define HASH(u) triple32(u)
//#define HASH(u) lowbias32(u)

uint uhash(uvec2 v)
{
//return uvec2(0u); // verify grid alignment
    return HASH((uint(v.x) + HASH(uint(v.y))));
}

// https://www.shadertoy.com/view/DtjyWD integer division - rounding down
ivec2 div_floor(ivec2 a, int b) // vector version thanks to Fabrice
{
    ivec2  S = (sign(abs(a*b))-sign(a*b))/2; // 0 if a*b >= 0
    return S * ((1 - abs(a)) / abs(b) - 1)+(1-S)*(a / b); // emulates ()?:
}
/*
// this implementation avoids operator % for stability on windows XXX differs from original at 0 ? XXX
ivec2 mod_positive(ivec2 a, int b)
{
    return a - div_floor(a, b) * b;
}
