// resolution of underlying grid
#define RES 8

struct block
{
    ivec2 v0;
    ivec2 v1;
    ivec2 id;
};

int block_hash(ivec2 tile, int x, int y, bool choose, bool mirror)
{
    ivec2 d = (mirror?ivec2(y, x):ivec2(x, y));
    return int(uhash(uvec2(tile+d)*2u+uint(choose)) % uint(RES))+(choose?d.y:d.x)*RES;
    //ivec2 r = ivec2(uhash(uvec2(tile+d)*2u+uint(choose)) % uint(RES))+d*RES;
    //return choose?r.y:r.x;
}

// Inspired by https://www.shadertoy.com/view/Ws3GRs Asymmetric Blocks by Shane,
// and also by https://www.shadertoy.com/view/tsVGRz Oddtiles (optimized) by stb.
// Checkerboard tiling of alternating (horizontal vs. vertical) (double-T-) crossings.
// Returns minimum, maximum, id.
// Severely compactified/crunched version.
block blocks_layout(ivec2 p) // https://www.shadertoy.com/view/mdcBR2 asymmetric blocks compactified by jt
{
    ivec2 tile = div_floor(p, RES); // global coordinates of tile
    ivec2 local = p - tile * RES;

    //bool flip = (tile.x % 2) != (tile.y % 2); // XXX breaks on windows XXX
    bool flip = (tile.x & 1) != (tile.y & 1); // true: vertical line goes through, false: horizontal line goes through
    //flip = !flip; // "dual" pattern (i.e. T-crossings flipped)

    // | identity
    // NW N NE
    //  W C E
    // SW S SE

    // - transposed (NOTE: y-coordinate up but matrix y-index down!)
    // SE W NE
    //  N C S
    // SW E NW

    if(flip) // horizontal line goes through: swap vertical with horizontal
    {
        local = local.yx;
    }

    // vertical line goes through (on flip transpose for horizontal line)

    //                 c
    //   +-+---------+---+-------+
    //   | |         |   |       |
    //   | |         |   #-------+ d
    // d +-#-#-------+---M       |
    //   |   |       |   |       |
    //   +---+-------+---+-------+
    //   |   |       |   |       |
    //   |   m-------+---#---#---+
    //   +---#       |       |   |
    //   |   |       |       |   |
    //   +---+-------+-------+---+

    int c = block_hash(tile, 0, 0, flip, flip); // offset of central line

    int x = 1-int(local.x < c); // [0,1]
    int X = 2*x-1; // [-1,+1]

    int d = block_hash(tile, X, 0,!flip, flip); // offset of branching line

    int y = 1-int(local.y < d); // [0,1]
    int Y = 2*y-1; // [-1,+1]

    ivec4 minmax =
        ivec4
        (
            // block corner in (0,0) tile
            c,
            d,
            // block corner in (X,Y) tile
            block_hash(tile,X,Y, flip, flip),
            block_hash(tile,0,Y,!flip, flip)
        );

    if(flip) tile = tile.yx;

    minmax += tile.xyxy * RES;

    ivec2 id = tile + ivec2(x,y);

    if(flip) // horizontal line goes through: unswap vertical with horizontal
    {
        minmax = minmax.yxwz;
        id = id.yx;
    }

    // fix order of min/max corners NOTE: defer division by 2 because center & extend could be half-integers!
    ivec2 center2 = minmax.xy+minmax.zw;
    ivec2 extent2 = abs(minmax.xy-minmax.zw);

    minmax.xy = (center2-extent2)/2;
    minmax.zw = (center2+extent2)/2;

    return block(minmax.xy, minmax.zw, id);
}

vec3 hash32(vec2 p) // https://www.shadertoy.com/view/4djSRW Hash without Sine by Dave_Hoskins
{
    vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yxz+33.33);
    return fract((p3.xxy+p3.yzz)*p3.zyx);
}

vec3 map(ivec2 v, vec2 l)
{
    block blk = blocks_layout(v.xy);
    uvec2 size = uvec2(blk.v1-blk.v0);

    uint cd = binary_gcd(size);

    vec3 color = hash32(vec2(blk.id));
    if(((blk.id.x^blk.id.y)&1)==0)
        color = 1.0 - color;

    ivec2 q = v.xy-blk.v0; // tile coordinate local to (minimum corner of) block
    color = mix(color, hash32(vec2(q/int(cd))), 0.5);
    float r = float(cd)/2.0;
    vec2 p = vec2(q % int(cd))+l-vec2(r);
    return color * vec3(smoothstep(0.,-float(RES*3)*3./iResolution.y , length(p)- r) ); // anti-aliasing thanks to FabriceNeyret2
    //return color * vec3(step(length(p), r));
}

#define scale (float(RES*3))

void mainImage(out vec4 fragColor, in vec2 p)
{
    vec2 R = iResolution.xy;
    p = (2.0 * p - R) / R.y;
    p *= scale;
    vec3 color = map(ivec2(floor(p)), fract(p));
    color = sqrt(color);
    fragColor = vec4(color, 1.0);
}
