// Martijn Steinrucken
//
// https://twitter.com/the_artofcode?lang=en
// https://www.youtube.com/TheArtOfCodeIsCool 
//
// https://www.youtube.com/watch?v=PGtv-dBi2wE
// https://www.youtube.com/watch?v=Ff0jJyyiVyw
// https://www.youtube.com/watch?v=AfKGMUDWfuE
// https://www.youtube.com/watch?v=Vmb7VGBVZJA
//
// https://michaelwalczyk.com/blog-ray-marching.html
// https://www.lcg.ufrj.br/cwdc/13-webgl/shaderToy.mp4
//
// A very simple ray marching implementation,
// with a few SDF functions: sphere, torus, box, cylinder.
// Boolean operations are also available: 
// union, intersection, difference and blend.
// 
// The beauty of the method is that there are no more polygons or vertices, 
// but objects are just scalar fields, given by Signed Distance Functions (SDFs).
//
// Paulo Roma
// 17/07/2022

#define MAX_STEPS 100
#define MAX_DIST 100.0
#define SURF_DIST 0.01
#define PI 3.1415925359
#define PERSISTENCE 0.5
#define LACUNARITY 1.0
#define FIRST 0.01
#define SECOND 0.01
#define OCTAVES 1.0
#define MAX_OCTAVES 8
#define EPSILON 0.00001

// Cellular noise ("Worley noise") in 3D in GLSL.
// Copyright (c) Stefan Gustavson 2011-04-19. All rights reserved.
// This code is released under the conditions of the MIT license.
// See LICENSE file for details.
// https://github.com/stegu/webgl-noise

// Modulo 289 without a division (only multiplications)
vec3 mod289(vec3 x) {
  return x - floor(x * (1.0 / 289.0)) * 289.0;
}

// Modulo 7 without a division
vec3 mod7(vec3 x) {
  return x - floor(x * (1.0 / 7.0)) * 7.0;
}

// Permutation polynomial: (34x^2 + 6x) mod 289
vec3 permute(vec3 x) {
  return mod289((34.0 * x + 10.0) * x);
}

// Cellular noise, returning F1 and F2 in a vec2.
// 3x3x3 search region for good F2 everywhere, but a lot
// slower than the 2x2x2 version.
// The code below is a bit scary even to its author,
// but it has at least half decent performance on a
// modern GPU. In any case, it beats any software
// implementation of Worley noise hands down.

vec2 cellular(vec3 P) {
#define K 0.142857142857 // 1/7
#define Ko 0.428571428571 // 1/2-K/2
#define K2 0.020408163265306 // 1/(7*7)
#define Kz 0.166666666667 // 1/6
#define Kzo 0.416666666667 // 1/2-1/6*2
#define jitter 1.0 // smaller jitter gives more regular pattern

	vec3 Pi = mod289(floor(P));
 	vec3 Pf = fract(P) - 0.5;

	vec3 Pfx = Pf.x + vec3(1.0, 0.0, -1.0);
	vec3 Pfy = Pf.y + vec3(1.0, 0.0, -1.0);
	vec3 Pfz = Pf.z + vec3(1.0, 0.0, -1.0);

	vec3 p = permute(Pi.x + vec3(-1.0, 0.0, 1.0));
	vec3 p1 = permute(p + Pi.y - 1.0);
	vec3 p2 = permute(p + Pi.y);
	vec3 p3 = permute(p + Pi.y + 1.0);

	vec3 p11 = permute(p1 + Pi.z - 1.0);
	vec3 p12 = permute(p1 + Pi.z);
	vec3 p13 = permute(p1 + Pi.z + 1.0);

	vec3 p21 = permute(p2 + Pi.z - 1.0);
	vec3 p22 = permute(p2 + Pi.z);
	vec3 p23 = permute(p2 + Pi.z + 1.0);

	vec3 p31 = permute(p3 + Pi.z - 1.0);
	vec3 p32 = permute(p3 + Pi.z);
	vec3 p33 = permute(p3 + Pi.z + 1.0);

	vec3 ox11 = fract(p11*K) - Ko;
	vec3 oy11 = mod7(floor(p11*K))*K - Ko;
	vec3 oz11 = floor(p11*K2)*Kz - Kzo; // p11 < 289 guaranteed

	vec3 ox12 = fract(p12*K) - Ko;
	vec3 oy12 = mod7(floor(p12*K))*K - Ko;
	vec3 oz12 = floor(p12*K2)*Kz - Kzo;

	vec3 ox13 = fract(p13*K) - Ko;
	vec3 oy13 = mod7(floor(p13*K))*K - Ko;
	vec3 oz13 = floor(p13*K2)*Kz - Kzo;

	vec3 ox21 = fract(p21*K) - Ko;
	vec3 oy21 = mod7(floor(p21*K))*K - Ko;
	vec3 oz21 = floor(p21*K2)*Kz - Kzo;

	vec3 ox22 = fract(p22*K) - Ko;
	vec3 oy22 = mod7(floor(p22*K))*K - Ko;
	vec3 oz22 = floor(p22*K2)*Kz - Kzo;

	vec3 ox23 = fract(p23*K) - Ko;
	vec3 oy23 = mod7(floor(p23*K))*K - Ko;
	vec3 oz23 = floor(p23*K2)*Kz - Kzo;

	vec3 ox31 = fract(p31*K) - Ko;
	vec3 oy31 = mod7(floor(p31*K))*K - Ko;
	vec3 oz31 = floor(p31*K2)*Kz - Kzo;

	vec3 ox32 = fract(p32*K) - Ko;
	vec3 oy32 = mod7(floor(p32*K))*K - Ko;
	vec3 oz32 = floor(p32*K2)*Kz - Kzo;

	vec3 ox33 = fract(p33*K) - Ko;
	vec3 oy33 = mod7(floor(p33*K))*K - Ko;
	vec3 oz33 = floor(p33*K2)*Kz - Kzo;

	vec3 dx11 = Pfx + jitter*ox11;
	vec3 dy11 = Pfy.x + jitter*oy11;
	vec3 dz11 = Pfz.x + jitter*oz11;

	vec3 dx12 = Pfx + jitter*ox12;
	vec3 dy12 = Pfy.x + jitter*oy12;
	vec3 dz12 = Pfz.y + jitter*oz12;

	vec3 dx13 = Pfx + jitter*ox13;
	vec3 dy13 = Pfy.x + jitter*oy13;
	vec3 dz13 = Pfz.z + jitter*oz13;

	vec3 dx21 = Pfx + jitter*ox21;
	vec3 dy21 = Pfy.y + jitter*oy21;
	vec3 dz21 = Pfz.x + jitter*oz21;

	vec3 dx22 = Pfx + jitter*ox22;
	vec3 dy22 = Pfy.y + jitter*oy22;
	vec3 dz22 = Pfz.y + jitter*oz22;

	vec3 dx23 = Pfx + jitter*ox23;
	vec3 dy23 = Pfy.y + jitter*oy23;
	vec3 dz23 = Pfz.z + jitter*oz23;

	vec3 dx31 = Pfx + jitter*ox31;
	vec3 dy31 = Pfy.z + jitter*oy31;
	vec3 dz31 = Pfz.x + jitter*oz31;

	vec3 dx32 = Pfx + jitter*ox32;
	vec3 dy32 = Pfy.z + jitter*oy32;
	vec3 dz32 = Pfz.y + jitter*oz32;

	vec3 dx33 = Pfx + jitter*ox33;
	vec3 dy33 = Pfy.z + jitter*oy33;
	vec3 dz33 = Pfz.z + jitter*oz33;

	vec3 d11 = dx11 * dx11 + dy11 * dy11 + dz11 * dz11;
	vec3 d12 = dx12 * dx12 + dy12 * dy12 + dz12 * dz12;
	vec3 d13 = dx13 * dx13 + dy13 * dy13 + dz13 * dz13;
	vec3 d21 = dx21 * dx21 + dy21 * dy21 + dz21 * dz21;
	vec3 d22 = dx22 * dx22 + dy22 * dy22 + dz22 * dz22;
	vec3 d23 = dx23 * dx23 + dy23 * dy23 + dz23 * dz23;
	vec3 d31 = dx31 * dx31 + dy31 * dy31 + dz31 * dz31;
	vec3 d32 = dx32 * dx32 + dy32 * dy32 + dz32 * dz32;
	vec3 d33 = dx33 * dx33 + dy33 * dy33 + dz33 * dz33;

	// Sort out the two smallest distances (F1, F2)
#if 0
	// Cheat and sort out only F1
	vec3 d1 = min(min(d11,d12), d13);
	vec3 d2 = min(min(d21,d22), d23);
	vec3 d3 = min(min(d31,d32), d33);
	vec3 d = min(min(d1,d2), d3);
	d.x = min(min(d.x,d.y),d.z);
	return vec2(sqrt(d.x)); // F1 duplicated, no F2 computed
#else
	// Do it right and sort out both F1 and F2
	vec3 d1a = min(d11, d12);
	d12 = max(d11, d12);
	d11 = min(d1a, d13); // Smallest now not in d12 or d13
	d13 = max(d1a, d13);
	d12 = min(d12, d13); // 2nd smallest now not in d13
	vec3 d2a = min(d21, d22);
	d22 = max(d21, d22);
	d21 = min(d2a, d23); // Smallest now not in d22 or d23
	d23 = max(d2a, d23);
	d22 = min(d22, d23); // 2nd smallest now not in d23
	vec3 d3a = min(d31, d32);
	d32 = max(d31, d32);
	d31 = min(d3a, d33); // Smallest now not in d32 or d33
	d33 = max(d3a, d33);
	d32 = min(d32, d33); // 2nd smallest now not in d33
	vec3 da = min(d11, d21);
	d21 = max(d11, d21);
	d11 = min(da, d31); // Smallest now in d11
	d31 = max(da, d31); // 2nd smallest now not in d31
	d11.xy = (d11.x < d11.y) ? d11.xy : d11.yx;
	d11.xz = (d11.x < d11.z) ? d11.xz : d11.zx; // d11.x now smallest
	d12 = min(d12, d21); // 2nd smallest now not in d21
	d12 = min(d12, d22); // nor in d22
	d12 = min(d12, d31); // nor in d31
	d12 = min(d12, d32); // nor in d32
	d11.yz = min(d11.yz,d12.xy); // nor in d12.yz
	d11.y = min(d11.y,d12.z); // Only two more to go
	d11.y = min(d11.y,d11.z); // Done! (Phew!)
	return sqrt(d11.xy); // F1, F2
#endif
}

float noise3(vec3 p) {
  vec2 v = cellular(p);
  return v.x * FIRST + v.y * SECOND; 
}


float octaveType (vec3 p) {
  float total = 0.;
  float frequency = 1.;
  float rangeOctave = 1.;
  float value = 0.;
  for (int i = 0; i < MAX_OCTAVES; ++i) {
    if (float(i) >= OCTAVES) break;
    value += noise3(p * frequency) * rangeOctave;
    total += rangeOctave;
    rangeOctave *= PERSISTENCE;
    frequency *= LACUNARITY;
  }
  return value / total;
}

vec3 normalnoise(vec3 p) {
  return normalize(vec3(octaveType(p+vec3(EPSILON,0.0,0.0))-octaveType(p-vec3(EPSILON,0.0,0.0)),
                          octaveType(p+vec3(0.0,EPSILON,0.0))-octaveType(p-vec3(0.0,EPSILON,0.0)),
                          octaveType(p+vec3(0.0,0.0,EPSILON))-octaveType(p-vec3(0.0,0.0,EPSILON))
                      ));
}


// Rotation matrix.
mat2 Rot (float a) {
    float s = sin(a);
    float c = cos(a);
    return mat2(c, -s, s, c);
}

// SDF of a torus at the origin given its two radius.
vec4 dTorus(vec3 p, vec2 r, vec3 col) {
    float x = length(p.xz) - r.x;

    return vec4(length(vec2(x, p.y)) - r.y, col);
}

vec4 sdEllipsoid(vec3 p, vec3 r, vec3 col) {
  float k0 = length(p/r);
  float k1 = length(p/(r*r));

  return vec4(k0*(k0-1.0)/k1, col);
}

// SDF of a box at the origin given its size (width, height and depth).
vec4 dBox(vec3 p, vec3 s, vec3 col) {
    return vec4(length(max(abs(p) - s, 0.0)), col);
}

// SDF of a sphere, given its center and radius.
vec4 dSphere(vec3 p, vec4 s, vec3 col) {
    return vec4(length(p - s.xyz) - s.w, col);
}

// SDF of a cylinder given a segment and a radius.
vec4 dCylinder(vec3 p, vec3 a, vec3 b, float r, vec3 col) {
    vec3 ab = b - a;
    vec3 ap = p - a;

    float t = dot(ab, ap) / dot(ab, ab);

    vec3 c = a + t * ab;

    float x = length(p - c) - r;
    float y = (abs(t - 0.5) - 0.5) * length(ab);
    float e = length(max(vec2(x, y), 0.0));
    float i = min(max(x, y), 0.0);

    return vec4(e + i, col);
}

vec4 sdRoundedCylinder( vec3 p, float ra, float rb, float h, vec3 col )
{
  vec2 d = vec2( length(p.xz)-2.0*ra+rb, abs(p.y) - h );
  return vec4(min(max(d.x,d.y),0.0) + length(max(d,0.0)) - rb, col);
}

vec4 sdOctahedron( vec3 p, float s, vec3 col)
{
  p = abs(p);
  float m = p.x+p.y+p.z-s;
  vec3 q;
       if( 3.0*p.x < m ) q = p.xyz;
  else if( 3.0*p.y < m ) q = p.yzx;
  else if( 3.0*p.z < m ) q = p.zxy;
  else return vec4(m*0.57735027, col);

  float k = clamp(0.5*(q.z-q.y+s),0.0,s); 
  return vec4(length(vec3(q.x,q.y-s+k,q.z-k)), col); 
}

// B + A
float unionSDF (float sda, float sdb) {
    return min(sda, sdb);
}

// B * A
float intersectionSDF (float sda, float sdb) {
    return max(sda, sdb);
}

// B - A
float differenceSDF (float sda, float sdb) {
    return max(-sda, sdb);
}

// Blend A and B
float blendSDF(float sda, float sdb, float k) {
    float h = clamp(0.5 + 0.5 * (sdb - sda) / k, 0.0, 1.0);
    return mix(sdb, sda, h) - k * h * (1.0 - h);
}

vec4 minWithColor(vec4 obj1, vec4 obj2) {
    if (obj2.x < obj1.x) return obj2;
    return obj1;
}

// Scene SDF: signed distance function.
vec4 getDist(vec3 p) {

    vec4 planeDist = vec4(p.y, vec3(0, .8, .8));

    float s = sin(iTime);

    // vent scene

    vec3 vent =  p-vec3(0,0,6);
    vent.xz *= Rot(5.0 * iTime);
    vec3 vent2 =  p-vec3(0,0,6);

    float boxOffset = 1.7 + (sin(1.2 * iTime)/1.2);
    vec3 box = p-vec3(0.8, boxOffset, 6);
    box.xy *= Rot(iTime*3.0);
    box.xz *= Rot(iTime*4.0);
    box.yz *= Rot(iTime*2.0);

    float boxOffset2 = 2.0 + cos(2.0 * iTime);
    vec3 box2 = p-vec3(-0.7, boxOffset2, 6.5);
    box2.xy *= Rot(iTime*4.0);
    box2.xz *= Rot(iTime*5.0);
    box2.yz *= Rot(iTime*2.0);

    float sphereOffset = 1.2 + (sin(0.8 * iTime)/1.8);
    vec3 sphere = p-vec3(-0.2, sphereOffset, 5);
    sphere.yz *= Rot(iTime);
    sphere.xz *= Rot(iTime);
    sphere.yz *= Rot(iTime);

    float donutOffset = 1.6 + (sin(3.0 * iTime)/0.8);
    vec3 donut = p-vec3(0, donutOffset, 6);
    donut.xz += vec2(2.0 * sin(iTime * 1.2), 2.0 * cos(iTime * 1.2));
    donut.xy *= Rot(iTime*9.0);
    donut.yz *= Rot(iTime*12.0);

    float cylinderOffset = 1.6 + (sin(3.0 * iTime)/1.5);
    vec3 cylinder = p-vec3(0, cylinderOffset, 6);
    cylinder.xz += vec2(1.5 * sin(iTime * 0.8), 1.5 * cos(iTime * 0.8));
    cylinder.xy *= Rot(iTime*2.0);
    cylinder.yz *= Rot(iTime);

    vec4 dVent = sdEllipsoid(vent, vec3(3,0.2,0.5), texture(iChannel0, vent.xz).xyz);
    vec4 dVent2 = sdEllipsoid(vent, vec3(0.5,0.2,3), texture(iChannel0, vent.xz).xyz);

    vec3 cor = normalnoise(vent);
    vec4 dLamp = dSphere(vent, vec4(0,0,0,0.65), cor.xxx);

    vec3 col1XZ = texture(iChannel2, box.xz*.5+.5).rgb;
    vec3 col1YZ = texture(iChannel2, box.yz*.5+.5).rgb;
    vec3 col1XY = texture(iChannel2, box.xy*.5+.5).rgb;
    vec3 n1 = normalize(box);
    vec3 textureBox1 =  col1YZ*n1.x + col1XZ*n1.y + col1XY*n1.z;
    vec4 dCaixa = dBox(box, vec3(0.2,0.2,0.2), textureBox1);

    vec3 col2XZ = texture(iChannel2, box2.xz*.5+.5).xyz;
    vec3 col2YZ = texture(iChannel2, box2.yz*.5+.5).xyz;
    vec3 col2XY = texture(iChannel2, box2.xy*.5+.5).xyz;
    vec3 n2 = normalize(box2);
    vec3 textureBox2 =  col2YZ*n2.x + col2XZ*n2.y + col2XY*n2.z;
    vec4 dCaixa2 = dBox(box2, vec3(0.15,0.15,0.15), textureBox2);

    vec4 dBola = dSphere(sphere, vec4(0,0,0, 0.25), texture(iChannel1, sphere.xz).xyz);

    vec4 dDonut = dTorus(donut, vec2(0.1,0.01), vec3(.8, .75, .0));
    vec4 dCylinder = sdRoundedCylinder(cylinder, 0.1, 0.1, 0.12, texture(iChannel1, cylinder.xz).xyz);

    // beyblade scene
    vec3 bey1 = p-vec3(0, 0.71, 6);
    bey1.xz += vec2(1.0 - (6.0 * sin(iTime)), 1.0 + (6.0 * cos(iTime)));
    bey1.xz *= Rot(10.0 * iTime);
    vec4 dBey1 = sdOctahedron(bey1, 0.7, texture(iChannel3, sphere.xz).xyz);

    vec4 d = minWithColor(dVent, planeDist);
    d = minWithColor(d, dVent2);
    d = minWithColor(d, dLamp);

    d = minWithColor(d, dCaixa);
    d = minWithColor(d, dCaixa2);
    d = minWithColor(d, dBola);
    d = minWithColor(d, dDonut);
    d = minWithColor(d, dCylinder);

    d = minWithColor(d, dBey1);

    return d;
}

// The normal is the gradient of the SDF scalar field at point p.
// By using finite difference: 
// https://en.wikipedia.org/wiki/Finite_difference#Relation_with_derivatives 
vec3 getNormal(vec3 p) {
    // arbitrary — should be smaller than any surface detail in your distance function, 
    // but not so small as to get lost in float precision
    vec2 e = vec2(0.001, 0);

    vec3 n = 
        vec3(
        getDist(p + e.xyy).x,
        getDist(p + e.yxy).x,
        getDist(p + e.yyx).x) - 
        vec3(
        getDist(p - e.xyy).x,
        getDist(p - e.yxy).x,
        getDist(p - e.yyx).x);

    return normalize(n);
}

// Walk through empty spheres until the ray hits an object
// or is beyond MAX_DIST, meaning no hit. 
vec4 rayMarch(vec3 ro, vec3 rd) {
    float dO = 0.0;
    vec4 dS;

    for (int i = 0; i < MAX_STEPS; i++) {
        vec3 p = ro + rd * dO;
        dS = getDist(p);
        dO += dS.x;
        if (dO > MAX_DIST || dS.x < SURF_DIST) break;
    }

    vec3 col = vec3(dS.yzw);

    return vec4(dO, col);
}

vec3 bumpMappedTexture(sampler2D bumpMap, vec3 tex, vec2 texCoord, float bumpDepth) {
  // Sample the bump map at the texture coordinate
  vec3 bump = texture(bumpMap, texCoord).rgb;

  // Calculate the perturbation vector by scaling the bump value and rotating it by 90 degrees
  vec2 perturb = bumpDepth * bump.rg;
  perturb = vec2(-perturb.y, perturb.x);

  // Perturb the texture coordinate and sample the texture
  vec3 texel = tex + vec3(perturb,0);

  return texel;
}

// Phong illumination model, given a point and camera position.
float getLight(vec3 p, vec3 ro) {
    vec3 lightPos = vec3(0, 5, 6);

    lightPos.xz += vec2(sin(iTime), cos(iTime)) * 2.0;

    vec3 l = normalize(lightPos - p);
    vec3 n = getNormal(p);
    n = normalize(bumpMappedTexture(iChannel0,n,p.xy,texture(iChannel0,p.xy).r));
    vec3 r = reflect(-l,n);

    // ambient component
    float amb = 0.1;
    // ambient reflection coefficient
    float ka = 1.0;

    // diffuse component - apply Lambert's cosine law
    float dif = clamp(dot(n, l), 0.0, 1.0);
    // diffuse reflection coefficient
    float kd = 0.8;

    // specular component
    float shininess = 100.;
    vec3 v = normalize(ro-p);
    float spec = clamp(dot(r, v), 0.0, 1.0);
    spec = pow(spec,shininess);
    // specular reflection coefficient
    float ks = 1.0;

    // slightly move point p in the direction of the normal,
    // so the distance is not zero. Otherwise, the first hit 
    // will be at the plane.
    vec4 d = rayMarch(p + 2. * n * SURF_DIST , l);

    // cast a ray to the light
    if (d.x < length(lightPos - p)) {
        // point in the shadow
        dif *= 0.1;
        spec *= 0.5;
    }

    // ambient + diffuse + specular components
    return ka * amb + kd * dif + ks * spec;
}

// Fragment shader.
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // normalized pixel coordinates (from -0.5 to 0.5)
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / min(iResolution.x, iResolution.y);

    // camera position
    vec3 ro = vec3(0, 2, 0);

    // projection plane z = 1
    vec3 rd = normalize(vec3(uv.x, uv.y, 1));

    // Rotate camera down about the x-axis
    rd.yz *= Rot(PI * 10. / 180.); 

    vec4 d = rayMarch(ro, rd);

    // point hit by the ray
    vec3 p = ro + rd * d.x;

    // illumination at point p
    float light_intensity = getLight(p, ro);

    // dark grey
    // vec3 background_color = vec3(.2, .2, .2);
    vec3 background_color = vec3(1).rgb;

    // Antique White color #FAEBD7
    vec3 surface_color = vec3(250, 235, 215) / 255.;

    // pixel color
    vec3 color;

    if (d.x < MAX_DIST) 
        // modulate surface color by the light intensity
        color = d.yzw * vec3(light_intensity);
    else 
        color = background_color;

    // output to screen
    fragColor = vec4(color,1.0);
}
