#define TAU 6.283185
#define PI  3.141593

#define SIN(x) (sin(mod(x,TAU)))
#define COS(x) (cos(mod(x,TAU)))

float N21(vec2 p)
{
    return fract(SIN(p.x * 997.0 + p.y * 9011.0) * 1051.0);
}

vec2 mysmoothedge2(vec2 p)
{
    return p * p * (3.0 - 2.0 * p);
}

float SmoothNoise21(vec2 uv)
{
    vec2 luv = fract(uv * 10.0); // local coordinate system
    luv = mysmoothedge2(luv);
    vec2 id = floor(uv * 10.0);

    float bl = N21(id);
    float br = N21(id + vec2(1,0));
    float b = mix(bl, br, luv.x);

    float tl = N21(id + vec2(0,1));
    float tr = N21(id + vec2(1,1));
    float t = mix(tl, tr, luv.x);

    float c = mix(b, t, luv.y);

    return c;
}

float SmoothNoiseOctave21(vec2 uv)
{
    float c = 0.0;
    //c += SmoothNoise21(uv * 1.0) * 1.0;
    c += SmoothNoise21(uv * 2.0) * 0.5;
    c += SmoothNoise21(uv * 4.0) * 0.25; 
    c += SmoothNoise21(uv * 8.0) * 0.125;
    c += SmoothNoise21(uv * 16.0) * 0.0625;
    c += SmoothNoise21(uv * 32.0) * 0.03125;
    //c += SmoothNoise21(uv * 64.0) * 0.0625;
    //c += SmoothNoise21(uv * 128.0) * 0.03125;

    c = c / 1.0;

    return c;
}

float N31(vec3 p)
{
    return fract(SIN(p.x * 997.0 + p.y * 9011.0 + p.z * 20551.0) * 1051.0);
}

vec3 mysmoothedge3(vec3 p)
{
    return p * p * (3.0 - 2.0 * p);
}

float SmoothNoise31(vec3 uvw)
{
    vec3 col = vec3(0);

    vec3 luv = fract(uvw * 4.0);
    luv = mysmoothedge3(luv);
    vec3 id = floor(uvw * 4.0);

    float xdir1 = mix(N31(id + vec3(0,0,0)),
                      N31(id + vec3(1,0,0)),
                      luv.x );
    float xdir2 = mix(N31(id + vec3(0,1,0)),
                      N31(id + vec3(1,1,0)),
                      luv.x );
    float xydir1 = mix(xdir1, xdir2, luv.y);

    float xdir3 = mix(N31(id + vec3(0,0,1)),
                      N31(id + vec3(1,0,1)),
                      luv.x );
    float xdir4 = mix(N31(id + vec3(0,1,1)),
                      N31(id + vec3(1,1,1)),
                      luv.x );
    float xydir2 = mix(xdir3, xdir4, luv.y);

    float xyzdir = mix(xydir1, xydir2, luv.z);

    return xyzdir;
}

float SmoothNoiseOctave31(vec3 uvw)
{
    float v = 2.0;
    float col = SmoothNoise31(uvw * v * 2.0);
    col += SmoothNoise31(uvw * v * 4.0) * 0.5;
    col += SmoothNoise31(uvw * v * 8.0) * 0.25;
    col += SmoothNoise31(uvw * v * 16.0) * 0.125;

    col /= 1.5;
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    vec3 col = vec3(0);

    vec3 uvw = vec3(uv, iTime / 50.0);

    col += SmoothNoiseOctave31(uvw);

    // Output to screen
    fragColor = vec4(col,1.0);
}
