// tweak from electro-prim's by @AlexWDunn
precision mediump float;

#define PI (3.1416)

float electro(vec2 pos, float d, float f, float o, float s, float a, float b, float x)
{
    float ti = iTime * s;

    float theta = atan(pos.y,pos.x);

    float amp = smoothstep(0., 1.0, (sin(theta+iTime*3.0+x)*0.6+2.0)-b)*a;
    float phase = d + sin(theta * f + o + ti *2.5) * amp;

    return sin(clamp(phase, 0.0, PI*2.0) + PI/2.0) + 0.9999;
}


float circle(vec2 pos, float r, float a, float o, float s, float f, float b, float t, float x)
{
    float d = length(pos);

    return 1.0 - smoothstep(0.0, t, electro(pos, d/r, f, o, s, a, b, x));
}


float shape(vec2 pos, float r, float a, float o, float s, float f, float b, float t, float x)
{
    float ci = circle(pos+vec2(0.0,0.0),r,a,o,s,f,b,t,x);
    return ci;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) 
{
   const float radius = 0.1;
   const float amp = 1.; 
   const float freq = 1.;
   const float band = 0.75;
   const float speed = 0.1;
   const float thickness = 0.03;

   vec2 pos = fragCoord.xy / max(iResolution.x, iResolution.y) * vec2(2.0) - vec2(1.0);
   pos -= vec2(0.0, -0.5);


   vec3 color = vec3(0.0);
   color.r = shape(pos, radius, amp, 0.1 *PI, 0. + speed, freq, band, thickness, 0.0);
   color.g = shape(pos, radius, amp, 0.0 *PI, 0.        , freq, band, thickness, 4.);
   color.b = shape(pos, radius, amp, 0.2 *PI, 0. - speed, freq, band, thickness, 7.);

   fragColor = vec4(color,1.0);
}
