vec3 palette( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d )
{
    return a + b*cos( 6.28318*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float aspect = iResolution.y/iResolution.x;

    vec2 uv = (fragCoord/iResolution.xy) * vec2(1.0, aspect);
    vec2 center = vec2(0.5, aspect * 0.5);



    float dist = length(uv - center);
    float a = smoothstep(1.0, -1.0, abs(sin(dist * 40.0 + iTime * 6.0)));
    float b = (sin(dist * 4.0 - iTime * 3.0) + 1.0) / 2.0;

    vec3 c1 = vec3(221.0 / 255.0, 254.0 / 255.0, 0.0 / 255.0);
    vec3 c2 = vec3(134.0 / 255.0, 148.0 / 255.0, 46.0 / 255.0);
    vec3 c3 = vec3(221.0 / 255.0, 254.0 / 255.0, 0.0 / 255.0);
    vec3 c4 = vec3(221.0 / 255.0, 254.0 / 255.0, 0.0 / 255.0);

    vec3 col = vec3(a) * palette(iTime * 3.0 + a, c1, c2, c3, c4 ) + vec3(b) * palette(iTime * 0.6 + b, c1, c2, c3, c4 );

    fragColor = vec4(col,1.0);
}
