float random(vec2 st)
{
    return fract(sin(dot(st.xy, vec2(12.9898,78.233))) * 43758.5453123);
}
bool DrawCube(vec2 uv,vec2 pos, vec2 size)
{
    float xyRatio = iResolution.y/iResolution.x;
    if(uv.x>pos.x-size.x && uv.x<pos.x+size.x
    && uv.y*xyRatio>pos.y*xyRatio-size.y && uv.y*xyRatio<pos.y*xyRatio+size.y)
    {
        return true;
    }
    else
    {
        return false;
    }
}
bool DrawCircle(vec2 uv,vec2 pos, float radius)
{
    radius = iResolution.x * radius;
    pos = vec2(pos.x*iResolution.x,pos.y*iResolution.y);
    uv= vec2(uv.x*iResolution.x,uv.y*iResolution.y);
    return distance(pos,uv)<radius;
}
bool DrawWave(vec2 uv,float frequency, float size, float height, float timeDilation)
{
    float pos = (sin(frequency*iTime+timeDilation+uv.x*3.0)*size)*0.5;

    pos = height + pos;
    if(uv.y<pos)
    {
        return true;
    }
    else
    {
        return false;
    }
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    fragColor = vec4(1,1,1,1);
    if(DrawWave(uv,2.0, 0.05, 0.91,1.0))
    {
        fragColor = vec4(0.1,0.5+abs(sin(iTime/3.0))*0.2,1,1);
    }
    if(DrawWave(uv,2.0, 0.05, 0.9,0.0))
    {
        fragColor = vec4(0.1,0.5+abs(sin(iTime/3.0))*0.2,1,1);
        //bubles
        if(DrawCircle(uv,vec2(0.1+0.1*sin((iTime+0.0)/2.0),fract((iTime+0.0)/2.0)), 0.025))
        {
            fragColor = vec4(0.9,0.9,0.9,1);
        }
        if(DrawCircle(uv,vec2(0.4+0.1*sin((iTime+2.3)/2.0),fract((iTime+2.0)/2.0)), 0.025))
        {
            fragColor = vec4(0.9,0.9,0.9,1);
        }
        if(DrawCircle(uv,vec2(0.6+0.1*sin((iTime+4.5)/2.0),fract((iTime+3.5)/2.0)), 0.025))
        {
            fragColor = vec4(0.9,0.9,0.9,1);
        }
        if(DrawCircle(uv,vec2(0.8+0.1*sin((iTime+7.2)/2.0),fract((iTime+5.25)/2.0)), 0.025))
        {
            fragColor = vec4(0.9,0.9,0.9,1);
        }
    }
    float submarineSpeed = sin(iTime*0.5)*0.025;
    float submarineHeight = 0.4 + sin(iTime*0.5)*0.1;

    //Bolhas do motor
    if(DrawCircle(uv,vec2(0.075+submarineSpeed - fract((iTime+5.25)*2.0),submarineHeight+ sin((iTime+5.25)/2.0)*0.15), 0.015))
    {
        fragColor = vec4(1,1,1,1);
    }
    if(DrawCircle(uv,vec2(0.075+submarineSpeed - fract((iTime+1.25)*1.2),submarineHeight+ sin((iTime+1.25)/2.0)*0.15), 0.015))
    {
        fragColor = vec4(1,1,1,1);
    }
    if(DrawCircle(uv,vec2(0.075+submarineSpeed - fract((iTime+9.25)*1.6),submarineHeight+ sin((iTime+9.25)/2.0)*0.15), 0.015))
    {
        fragColor = vec4(1,1,1,1);
    }
    if(DrawCircle(uv,vec2(0.075+submarineSpeed - fract((iTime+3.25)*1.4),submarineHeight+ sin((iTime+3.25)/2.0)*0.15), 0.015))
    {
        fragColor = vec4(1,1,1,1);
    }
    if(DrawCircle(uv,vec2(0.075+submarineSpeed - fract((iTime+4.25)*1.5),submarineHeight+ sin((iTime+4.25)/2.0)*0.15), 0.015))
    {
        fragColor = vec4(1,1,1,1);
    }
    if(DrawCircle(uv,vec2(0.075+submarineSpeed - fract((iTime+2.25)*1.3),submarineHeight+ sin((iTime+2.25)/2.0)*0.15), 0.015))
    {
        fragColor = vec4(1,1,1,1);
    }

    //Areia
    if(DrawWave(uv,0.5, 0.05, 0.05,0.0))
    {
        fragColor = vec4(0.8+ 0.1*random(uv),0.8+0.1*random(uv),0.4+0.1*random(uv),1);
    }
    //Corpo
    if(DrawCube(uv,vec2(0.3+submarineSpeed,submarineHeight), vec2(0.1,0.1)))
    {
        fragColor = vec4(0.75,0.75,0.75,1);
    }
    //Motor
    if(DrawCube(uv,vec2(0.1+submarineSpeed,submarineHeight), vec2(0.025,0.025)))
    {
        fragColor = vec4(0.75,0.75,0.75,1);
    }
    if(DrawCube(uv,vec2(0.075+submarineSpeed,submarineHeight), vec2(0.01, 0.03+0.05*abs(sin(iTime*20.0)))))
    {
        fragColor = vec4(0.75,0.75,0.75,1);
    }
    //Antena
    if(DrawCube(uv,vec2(0.375+submarineSpeed,0.24+submarineHeight), vec2(0.04,0.015)))
    {
        fragColor = vec4(0.85,0.95,0.95,1);
    }
     if(DrawCube(uv,vec2(0.35+submarineSpeed,0.1+submarineHeight), vec2(0.025,0.1)))
    {
        fragColor = vec4(0.75,0.75,0.75,1);
    }
    //Pontas Circulos
    if(DrawCircle(uv,vec2(0.4+submarineSpeed,submarineHeight), 0.1))
    {
        fragColor = vec4(0.85,0.95,0.95,1);
    }
    if(DrawCircle(uv,vec2(0.2+submarineSpeed,submarineHeight), 0.1))
    {
        fragColor = vec4(0.75,0.75,0.75,1);
    }
}
