// (C) Copyright 2021 by Yury Ershov

#define OUTPUT_AVG     1    // Average 4 buffers or use only one
#define SMOOTH         1    // Smooth output: 0=no; 1=slightly; 2=more
#define PALETTE        0    // 0=fire, 1=blue, 2=green

vec3 col(float x) {
  return vec3(
#if PALETTE == 0
      clamp(x, 0., 1./3.),
      clamp(x-1./3., 0., 1./3.),
      clamp(x-2./3., 0., 1./3.)
#elif PALETTE == 1
      clamp(x-2./3., 0., 1./3.),
      clamp(x-1./3., 0., 1./3.),
      clamp(x, 0., 1./3.)
#elif PALETTE == 2
      clamp(x-2./3., 0., 1./3.),
      clamp(x, 0., 1./3.),
      clamp(x-1./3., 0., 1./3.)
#endif
   ) * 3.;
}

