// EOM

#define min4(a1,a2,a3,a4)               min(min(a1,a2),min(a3,a4))
#define min8(a1,a2,a3,a4,b1,b2,b3,b4)   min(min4(a1,a2,a3,a4),min4(b1,b2,b3,b4))
#define max4(a1,a2,a3,a4)               max(max(a1,a2),max(a3,a4))
#define max8(a1,a2,a3,a4,b1,b2,b3,b4)   max(max4(a1,a2,a3,a4),max4(b1,b2,b3,b4))
#define AVG \
  float mn = min8( \
          V(-s.x, -s.y),  V( 0.0, -s.y),     V( s.x, -s.y), \
          V(-s.x,  0.0),                     V( s.x,  0.0), \
          V(-s.x,  s.y),  V( 0.0,  s.y),     V( s.x,  s.y)); \
  float mx = max8( \
          V(-s.x, -s.y),  V( 0.0, -s.y),     V( s.x, -s.y), \
          V(-s.x,  0.0),                     V( s.x,  0.0), \
          V(-s.x,  s.y),  V( 0.0,  s.y),     V( s.x,  s.y)); \
  float avg = \
         (V(-s.x, -s.y) + V( 0.0, -s.y)*2. + V( s.x, -s.y) + \
          V(-s.x,  0.0)*2.                 + V( s.x,  0.0)*2. + \
          V(-s.x,  s.y) + V( 0.0,  s.y)*2. + V( s.x,  s.y)) / 12.; \
  float pt = V(0.0, 0.0); \
  float minmaxdiff = clamp(distance(mn,mx), 0.01, 0.67); \
  float diff = distance(avg, pt); \
  return mix(pt, avg, smoothstep(0.0, 1.0, diff / minmaxdiff)); \
// EOM

#define V(X,Y)   texture(iChannel0, c + vec2(X, Y)).r
float avg0(in vec2 c, in vec2 s) { AVG }
#undef V
#define V(X,Y)   texture(iChannel1, c + vec2(X, Y)).r
float avg1(in vec2 c, in vec2 s) { AVG }
#undef V
#define V(X,Y)   texture(iChannel2, c + vec2(X, Y)).r
float avg2(in vec2 c, in vec2 s) { AVG }
#undef V
#define V(X,Y)   texture(iChannel3, c + vec2(X, Y)).r
float avg3(in vec2 c, in vec2 s) { AVG }
#undef V

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    fragColor.a = 1.0;

#if OUTPUT_AVG
#if SMOOTH
    vec2 s = vec2(1., 1.) / iResolution.xy;
	fragColor.rgb = col(
            (avg0(uv, s) +
             avg1(uv, s) +
             avg2(uv, s) +
             avg3(uv, s)) / 4.
        );
#else
	fragColor.rgb = col(
            (texture(iChannel0,uv).r +
            texture(iChannel1,uv).r +
            texture(iChannel2,uv).r +
            texture(iChannel3,uv).r) / 4.
        );
#endif

#else

#if SMOOTH
	fragColor.rgb = col(avg0(uv, vec2(1., 1.) / iResolution.xy));
#else
	fragColor.rgb = col(texture(iChannel0,uv).r);
#endif

#endif
}

// (C) Copyright 2021 by Yury Ershov

#define CLICK_TO_RESET  1     // Click mouse to reset the field.
#define LOOKUP_DIST     2     // 1 => 1; 2 => 2, no corners; 3 => 2 with corners.
#define AVG_EVERY_N     0     // Average every THIS frame; 0 => none
#define FLARE_UP        1     // Cause the fire to flare up
#define SPEED           1.
#define RAND            (hash12(uv)-0.5)/100.

// https://www.shadertoy.com/view/4djSRW
float hash12(vec2 position)
{
    vec2 p = (position + mod(iTime, 200.) * 1500. + 50.0);
    vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}

#if FLARE_UP
#define COORD (1.-uv.y)*SPEED
#else
#define COORD 1.*SPEED
#endif

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;

    if(iFrame < 2
#if CLICK_TO_RESET
       || iMouse.z > 0.
#endif
       ) {
        fragColor = vec4(vec3(pow(1.-uv.y, 4.)), 1.-pow(uv.y, 10.));
        return;
    }

    if (
      //length(iMouse.xy - fragCoord) > 10. &&
#if AVG_EVERY_N > 0
      (iFrame % AVG_EVERY_N != 0) &&
#endif
      hash12(fragCoord) > 0.05*COORD
    ) {
        fragColor = texture(iChannel0,uv);
        return;
    }

    vec2 s1 = vec2(1., 1.) / iResolution.xy;
    vec2 d = uv;
    float v = texture(iChannel0, d).a;
    float vv; vec2 dd;
#define TEST(DX, DY)  \
    dd = uv + vec2(DX, DY); \
    vv = texture(iChannel0, dd).a; \
    if (vv > v) { v = vv; d = dd; } \
// EOM
    TEST( s1.x,    0.);
    TEST(-s1.x,    0.);
    TEST(   0.,  s1.y);
    TEST(   0., -s1.y);
    TEST(-s1.x, -s1.y);
    TEST( s1.x,  s1.y);
    TEST(-s1.x,  s1.y);
    TEST( s1.x, -s1.y);
#if LOOKUP_DIST > 1
    vec2 s2 = s1 * 2.;
    TEST( s2.x,   0.);
    TEST(-s2.x,   0.);
    TEST(   0., s2.y);
    TEST(   0.,-s2.y);
    TEST( s2.x, s1.y);
    TEST(-s2.x,-s1.y);
    TEST(-s1.x, s2.y);
    TEST( s1.x,-s2.y);
    TEST( s2.x,-s1.y);
    TEST(-s2.x, s1.y);
    TEST( s1.x, s2.y);
    TEST(-s1.x,-s2.y);
#if LOOKUP_DIST > 2
    TEST( s2.x, s2.y);
    TEST(-s2.x,-s2.y);
    TEST( s2.x,-s2.y);
    TEST(-s2.x, s2.y);
#endif
#endif

    float c = clamp(texture(iChannel0, d).r + RAND, 0., 1.);
    fragColor = vec4(c,0,0,v+0.0001);
}

