// [zznewclear13] Multiple-Scattering Image-Based Lighting
// https://www.shadertoy.com/view/mlXyRs
// Multiple-scattering image based lighting. Computes DFG texture and SH on the fly.
// BufferA and BufferB can be pre-computed in practice.
// Furnace test mode can be enabled in Common tab.
// Top: multiple-scattering; Bottom: single-scattering; Left: conductor; Right: dielectric.
// The random walk approach should be more accurate?

// Multple-Scattering References
// A Multiple-Scattering Microfacet Model for Real-Time Image-based Lighting, Carmelo J. Fdez-Aguera
// https://jcgt.org/published/0008/01/03/
// https://google.github.io/filament/Filament.html#materialsystem/improvingthebrdfs

// IBL References:
// https://google.github.io/filament/Filament.html#lighting/imagebasedlights
// https://bruop.github.io/ibl/

// Post-processing.
// Narkowicz 2015, "ACES Filmic Tone Mapping Curve"
vec3 Tonemap_ACES(const vec3 x) {
    const float a = 2.51;
    const float b = 0.03;
    const float c = 2.43;
    const float d = 0.59;
    const float e = 0.14;
    return (x * (a * x + b)) / (x * (c * x + d) + e);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{  
    vec2 uv = fragCoord / iResolution.xy;
    vec3 color = texture(iChannel2, uv).rgb;

    color = Tonemap_ACES(color);
    color = pow(color, vec3(0.4545));

    // vec3 bufferA = texture(iChannel0, uv).rgb;
    // vec3 bufferB = texture(iChannel1, uv).rgb;

    fragColor = vec4(color, 1.0);
}

// Commenting/Uncommenting this line requires rewinding to reset buffers.
// #define FURNACE_TEST
#define FURNACE_COLOR vec3(0.5f)

#define MULTIPLE_SCATTERING

#define GAMMA 2.2f
#define PI 3.14159265359
#define INV_PI 0.31830988618
#define SQRT2_2 0.70710678118

#define DIELETRIC_REFLECTANCE 0.04

/////////////////////////////////////////
//              Structs                //
/////////////////////////////////////////

struct SurfaceInput
{
    vec3 positionWS;
    vec3 normalWS;
    vec3 viewWS;
    vec3 lightWS;
    vec3 halfVec;
    vec3 reflWS;

    float NoV;
    float NoL;
    float NoH;
    float LoV;
    float LoH;
};

struct MaterialInput
{
    vec3 diffuse;
    float alpha;
    vec3 specular;
    float perceptualRoughness;
    float roughness;
    float metallic;
    float occlusion;
    float f90;
};

float F90(vec3 f0)
{
	// return clamp(dot(f0, 50.0 * 0.33), 0.0, 1.0);
	return clamp(50.0 * f0.g, 0.0, 1.0);
}

SurfaceInput InitSurfaceInput(vec3 positionWS, vec3 normalWS, vec3 viewWS, vec3 lightWS)
{
	SurfaceInput surfaceInput;
	surfaceInput.positionWS = positionWS;
	surfaceInput.normalWS = normalWS;
	surfaceInput.viewWS = viewWS;
	surfaceInput.lightWS = lightWS;
    surfaceInput.halfVec = normalize(viewWS + lightWS);
	surfaceInput.reflWS = reflect(-viewWS, normalWS);

	// PBR Diffuse Lighting for GGX+Smith Microsurfaces, Earl Hammon, Jr.
	// surfaceInput.NoV = dot(normalWS, viewWS);
	// surfaceInput.NoL = dot(normalWS, lightWS);
	// surfaceInput.LoV = dot(lightWS, viewWS);
	// float lenSqLV = 2.0f + 2.0f * surfaceInput.LoV;
	// float rcpLenLV = inversesqrt(lenSqLV);
	// surfaceInput.NoH = (surfaceInput.NoL + surfaceInput.NoV) * rcpLenLV;
    // 
	// surfaceInput.LoH = rcpLenLV + rcpLenLV * surfaceInput.LoV;
    surfaceInput.NoV = abs(dot(normalWS, viewWS)) + 1e-5f;
    surfaceInput.NoL = max(dot(normalWS, lightWS), 1e-5f);
    surfaceInput.NoH = dot(normalWS, surfaceInput.halfVec);
    surfaceInput.LoV = dot(lightWS, viewWS);
    surfaceInput.LoH = dot(lightWS, surfaceInput.halfVec);

	return surfaceInput;
}

MaterialInput InitMaterialInput(vec4 albedoAlpha, vec3 rmo)
{
	MaterialInput materialInput;
	materialInput.perceptualRoughness = rmo.x;
	materialInput.roughness = rmo.x * rmo.x;
	materialInput.metallic = rmo.y;
	materialInput.occlusion = rmo.z;

	// https://google.github.io/filament/Filament.html#materialsystem/parameterization
	materialInput.diffuse = albedoAlpha.rgb * (1.0f - materialInput.metallic);
	materialInput.specular = mix(vec3(DIELETRIC_REFLECTANCE), albedoAlpha.rgb, materialInput.metallic);
	materialInput.f90 = F90(materialInput.specular);

	materialInput.alpha = albedoAlpha.a;
	return materialInput;
}

/////////////////////////////////////////
//                BRDF                 //
/////////////////////////////////////////

// Bruce Walter et al. 2007. Microfacet Models for Refraction through Rough Surfaces. Proceedings of the Eurographics Symposium on Rendering.
float D_GGX(float NoH, float roughness) {
    float a = NoH * roughness;
    float k = roughness / (1.0 - NoH * NoH + a * a);
    return k * k * INV_PI;
}

// Eric Heitz. 2014. Understanding the Masking-Shadowing Function in Microfacet-Based BRDFs. Journal of Computer Graphics Techniques, 3 (2).
float V_SmithGGXCorrelated(float NoV, float NoL, float roughness) {
    float a2 = roughness * roughness;
    float GGXV = NoL * sqrt(NoV * NoV * (1.0 - a2) + a2);
    float GGXL = NoV * sqrt(NoL * NoL * (1.0 - a2) + a2);
    return 0.5 / (GGXV + GGXL);
}

// Christophe Schlick. 1994. An Inexpensive BRDF Model for Physically-Based Rendering. Computer Graphics Forum, 13 (3), 233–246.
float F_Schlick(float LoH, float f0, float f90) {
    return f0 + (f90 - f0) * pow(1.0 - LoH, 5.0);
}

vec3 F_Schlick(float LoH, vec3 f0, vec3 f90) {
    return f0 + (f90 - f0) * pow(1.0 - LoH, 5.0);
}

vec3 F_Schlick(float LoH, vec3 f0) {
    return f0 + (vec3(1.0) - f0) * pow(1.0 - LoH, 5.0);
}

vec3 F0(vec3 baseColor, float metallic) {
    return DIELETRIC_REFLECTANCE * (1.0 - metallic) + baseColor * metallic;
}

// GGX Specular
vec3 Fr_GGX(float NoV, float NoL, float NoH, float LoH, float roughness, vec3 f0) {
    float D = D_GGX(NoH, roughness);
    vec3 F = F_Schlick(LoH, f0);
    float V = V_SmithGGXCorrelated(NoV, NoL, roughness);
    return D * F * V;
}   

// Lambert Diffuse
float Fd_Lambert() {
    return INV_PI;
}

// Burley Diffuse
// Brent Burley. 2012. Physically Based Shading at Disney. Physically Based Shading in Film and Game Production, ACM SIGGRAPH 2012 Courses.
float Fd_Burley(float NoV, float NoL, float LoH, float roughness) {
    float f90 = 0.5 + 2.0 * roughness * LoH * LoH;
    float lightScatter = F_Schlick(NoL, 1.0, f90);
    float viewScatter = F_Schlick(NoV, 1.0, f90);
    return lightScatter * viewScatter * INV_PI;
}

/////////////////////////////////////////
//        Importance Sampling          //
/////////////////////////////////////////

//Random functions
float seed;
float GetRandom(){return fract(sin(seed++)*43758.5453123);}
float hash12(vec2 p)
{
	vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}

vec3 Eval_GGX(SurfaceInput si, MaterialInput mi)
{
    float D = D_GGX(si.NoH, mi.roughness);
    vec3 F = F_Schlick(si.LoH, mi.specular);
    float V = V_SmithGGXCorrelated(si.NoV, si.NoL, mi.roughness);
    return D * F * V;
}

float PDF_GGX(SurfaceInput si, MaterialInput mi)
{
    float D = D_GGX(si.NoH, mi.roughness);
    return D * si.NoH / (4.0f * si.LoH);
}

vec3 SampleHemiSphereGGX(float roughness)
{
    float randomA = GetRandom();
    float randomB = GetRandom();

    // Add 1e-5 to solve some mysterious bug on Metal
    float theta = acos(sqrt((1.0 - randomA) / ((roughness * roughness - 1.0) * randomA + 1.0 + 1e-5)));
    float phi = 2.0 * PI * randomB;
    float sinTheta = sin(theta);

    return vec3(cos(phi) * sinTheta, sin(phi) * sinTheta, cos(theta));
}

SurfaceInput Sample_GGX(vec3 normalWS, vec3 viewWS, MaterialInput mi)
{
    float randomValue = GetRandom();
    vec3 w = normalWS;
    vec3 s, u, v;
    s = SampleHemiSphereGGX(mi.roughness);
    s.z = abs(s.z);
    if(abs(dot(normalWS, vec3(0.0, 1.0, 0.0))) <= SQRT2_2)
    {
        u = normalize(cross(vec3(0.0, 1.0, 0.0), w));
        v = cross(w, u);
    }
    else
    {
        v = normalize(cross(w, vec3(1.0, 0.0, 0.0)));
        u = cross(v, w);
    }
    vec3 halfVec = s.x * u + s.y * v + s.z * w;
    vec3 lightWS = reflect(-viewWS, halfVec);
    SurfaceInput si = InitSurfaceInput(vec3(0.0f, 0.0f, 0.0f), normalWS, viewWS, lightWS);
    return si;
}

// Generates DFG lut.

#define SAMPLE_COUNT 1.0f

// Importance sampling ggx brdf
// Fr / P * NoL
//
// = (Fr/F) / P * NoL * F
//
// = (D * G / (4.0f * NoV * NoL)) / (D * NoH / (4.0f * LoH)) * NoL * F
//   +----------Fr/F------------+   +-----------P----------+
//
// = (G / (4.0f * NoV * NoL)) * (4.0f * LoH * NoL / NoH) * F
//   +----------V-----------+
//
// = 4.0f * V * LoH * NoL / NoH * F
//   +-------Eval_GGX_E-------+
float Eval_GGX_E(SurfaceInput si, MaterialInput mi)
{
    float a2 = mi.roughness * mi.roughness;
    float GGXL = si.NoV * sqrt((-si.NoL * a2 + si.NoL) * si.NoL + a2);
    float GGXV = si.NoL * sqrt((-si.NoV * a2 + si.NoV) * si.NoV + a2);
    return (2.0f * si.NoL * si.LoH / si.NoH) / (GGXV + GGXL);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 color = vec4(0.0, 0.0, 0.0, 0.0f);
    if(iFrame == 0)
    {
        fragColor = color; return;
    }

    vec2 uv = fragCoord / iResolution.xy;
    seed = hash12(fragCoord) + iTime;  
    MaterialInput mi = InitMaterialInput(vec4(1.0f, 1.0f, 1.0f, 1.0f), vec3(uv.y, 1.0f, 1.0f));
    vec3 normalWS = vec3(0.0f, 1.0f, 0.0f);
    vec3 viewWS = vec3(sqrt(1.0f - uv.x * uv.x), uv.x, 0.0f);

    vec2 sum = vec2(0.0f);
    for(float i=0.0f; i<SAMPLE_COUNT; ++i)
    {
        SurfaceInput si = Sample_GGX(normalWS, viewWS, mi);
        float Gv = Eval_GGX_E(si, mi);
        float Fc = pow(1.0f - si.LoH, 5.0f);

        // F = F0 + (F90-F0) * Fc
        // F = F0 * (F90 - Fc) + F90 * Fc, F90 ≈ 1.0f
        sum += Gv * vec2(1.0f - Fc, Fc);
    }
    sum /= SAMPLE_COUNT;

    vec4 bufferA = texelFetch(iChannel0, ivec2(fragCoord), 0);
    bufferA.a += 1.0f;
    color.rg = mix(bufferA.rg, sum, 1.0f / bufferA.a);
    fragColor = vec4(color.rgb, bufferA.a);
}

// Generates SH.
// An Efficient Representation for Irradiance Environment Maps, Ravi Ramamoorthi, Pat Hanrahan
// https://cseweb.ucsd.edu/~ravir/papers/envmap/envmap.pdf

#define SAMPLE_COUNT 32.0f

float Evaluate(vec3 normal, int index)
{
    switch(index)
    {
        case 0:
            return 0.2820947917f;
        case 1:
            return 0.4886025119f * normal.y;
        case 2:
            return 0.4886025119f * normal.z;
        case 3:
            return 0.4886025119f * normal.x;
        case 4:
            return 1.0925484306f * normal.x * normal.y;
        case 5:
            return 1.0925484306f * normal.y * normal.z;
        case 6:
            return 0.3153915652f * (3.0f * normal.z * normal.z - 1.0f);
        case 7:
            return 1.0925484306f * normal.x * normal.z;
        case 8:
            return 0.5462742153f * (normal.x * normal.x - normal.y * normal.y);
        default:
            return 0.2820947917f;
    }
    return 0.0f;
}

vec3 SampleSphere(vec2 rv)
{
    float theta = rv.x * PI * 2.0f;
    float u = rv.y * 2.0f - 1.0f;
    float v = sqrt(1.0f - u * u);
    return vec3(v * cos(theta), v * sin(theta), u);
}

vec3 sampleBackgroundLod(samplerCube channel, vec3 rd, float lod, float gamma)
{
#if defined(FURNACE_TEST)
    return FURNACE_COLOR;
#else
    return pow(textureLod(channel, normalize(rd), lod).rgb, vec3(gamma));
#endif
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 col = vec3(0.0f);
    ivec2 iFragCoord = ivec2(fragCoord);
    if (iFrame == 0 || any(greaterThanEqual(iFragCoord, ivec2(9, 1))))
    {
        fragColor = vec4(col, 0.0f);
        return;
    }

    vec2 uv = fragCoord / iResolution.xy;
    seed = hash12(fragCoord) + iTime;
    int shIndex = iFragCoord.x % 9;

    for (float i=0.0f; i<SAMPLE_COUNT; ++i)
    {
        vec3 normal = normalize(SampleSphere(vec2(GetRandom(), GetRandom())));
        float shVal = Evaluate(normal, shIndex);
        vec3 color = sampleBackgroundLod(iChannel0, normal, 0.0f, GAMMA);
        col += shVal * color;
    }
    col /= SAMPLE_COUNT;
    col *= 4.0f; // No idea why, maybe sphere surface area / projectd hemisphere area (4pi/pi)?

    vec4 bufferB = texelFetch(iChannel1, iFragCoord, 0);
    bufferB.a += 1.0f;
    col = mix(bufferB.rgb, col, 1.0f / bufferB.a);
    fragColor = vec4(col, bufferB.a);
}

// Calculates multiple-scattering image-based lighting.

const float tmin = 1e-3;
const float tmax = 1e3;

mat3 setCamera( in vec3 ro, in vec3 ta, float cr )
{
	vec3 cw = normalize(ta-ro);
	vec3 cp = vec3(sin(cr), cos(cr),0.0);
	vec3 cu = normalize( cross(cw,cp) );
	vec3 cv =          ( cross(cu,cw) );
    return mat3( cu, cv, cw );
}

// Special case: sphere with radius of 1.0 at (0.0, 0.0, 0.0)
bool raySphereIntersect(vec3 o, vec3 d, out vec3 p)
{
    bool hasIntersection = false;
    float a = dot(d, d);
    float b = dot(o, d);
    float c = dot(o, o) - 1.0f;
    float det = b * b - a * c;    
    if(det < 0.0f) return false;

    float sqrtVal = sqrt(det);
    float t1 = (-b - sqrtVal) / a;
    float t2 = (-b + sqrtVal) / a;
    if(t2 <= tmin) return false;

    float t = (t1 <= tmin) ? t2 : t1;  
    if(t > tmax) return false;

    p = normalize(o + t * d);
    return true;
}

vec3 sampleBackground(samplerCube channel, vec3 rd, float gamma)
{
#if defined(FURNACE_TEST)
    return FURNACE_COLOR;
#else
    return pow(texture(channel, normalize(rd)).rgb, vec3(gamma));
#endif
}

vec3 sampleBackgroundLod(samplerCube channel, vec3 rd, float lod, float gamma)
{
#if defined(FURNACE_TEST)
    return FURNACE_COLOR;
#else
    return pow(textureLod(channel, normalize(rd), lod).rgb, vec3(gamma));
#endif
}

vec3 CalcIrradiance(vec3 nor) { 
    float c1 = 0.429043;
    float c2 = 0.511664;
    float c3 = 0.743125;
    float c4 = 0.886227;
    float c5 = 0.247708;

    vec3 coeffs0 = texelFetch(iChannel2, ivec2(0, 0), 0).rgb;
    vec3 coeffs1 = texelFetch(iChannel2, ivec2(1, 0), 0).rgb;
    vec3 coeffs2 = texelFetch(iChannel2, ivec2(2, 0), 0).rgb;
    vec3 coeffs3 = texelFetch(iChannel2, ivec2(3, 0), 0).rgb;
    vec3 coeffs4 = texelFetch(iChannel2, ivec2(4, 0), 0).rgb;
    vec3 coeffs5 = texelFetch(iChannel2, ivec2(5, 0), 0).rgb;
    vec3 coeffs6 = texelFetch(iChannel2, ivec2(6, 0), 0).rgb;
    vec3 coeffs7 = texelFetch(iChannel2, ivec2(7, 0), 0).rgb;
    vec3 coeffs8 = texelFetch(iChannel2, ivec2(8, 0), 0).rgb;

    return (
        c1 * coeffs8 * (nor.x * nor.x - nor.y * nor.y) +
        c3 * coeffs6 * nor.z * nor.z +
        c4 * coeffs0 -
        c5 * coeffs6 +
        2.0f * c1 * coeffs4 * nor.x * nor.y +
        2.0f * c1 * coeffs7 * nor.x * nor.z +
        2.0f * c1 * coeffs5 * nor.y * nor.z +
        2.0f * c2 * coeffs3 * nor.x +
        2.0f * c2 * coeffs1 * nor.y +
        2.0f * c2 * coeffs2 * nor.z
        );
}

vec3 render(vec3 ro, vec3 rd)
{
    vec3 p = vec3(0.0f, 1.0f, 0.0f);
    bool hit = raySphereIntersect(ro, rd, p);
    vec3 tBackground = sampleBackground(iChannel0, rd, GAMMA);
    if(!hit) return tBackground;

    vec3 positionWS = p;
    vec3 normalWS = normalize(p);
    vec3 viewWS = -normalize(rd);
    vec3 lightWS = normalize(vec3(0.5f, 2.0f, 0.2f));
    SurfaceInput si = InitSurfaceInput(positionWS, normalWS, viewWS, lightWS);

    float time = abs(fract(iTime * 0.2f) - 0.5f) * 2.0f;
    // gold
    vec3 albedo1 = vec3(0.944f, 0.76f, 0.373f);
    float roughness1 = mix(0.3f, 1.0f, time);
    float metallic1 = 0.95f;
    // plastic
    vec3 albedo2 = vec3(0.95f, 0.3f, 0.2f);
    float roughness2 = mix(0.7f, 0.0f, time);
    float metallic2 = 0.0f;
    float weightz = step(0.0f, si.positionWS.z);
    float weighty = step(0.0f, si.positionWS.y + 0.25f);

#if defined(FURNACE_TEST)
    vec3 albedo = vec3(1.0f, 1.0f, 1.0f);
    // Energy loss when material is "semi-conductor".
    float metallic = 1.0f - step(0.5f, weightz);
#else
    vec3 albedo = mix(albedo1, albedo2, vec3(weightz)); 
    float metallic = mix(metallic1, metallic2, weightz);
#endif
    float roughness = mix(roughness1, roughness2, weightz);

    MaterialInput mi = InitMaterialInput(vec4(albedo, 1.0f), vec3(roughness, metallic, 1.0f));

    vec2 dfg = texture(iChannel1, vec2(si.NoV, mi.perceptualRoughness)).xy;
    vec3 envDiffuse = CalcIrradiance(si.normalWS);
    float lod = mi.perceptualRoughness * 8.0f;
    // Should sample a prefiltered background image.
    vec3 envSpecular = sampleBackgroundLod(iChannel0, si.reflWS, lod, GAMMA);

    vec3 envFd;
    vec3 envFr;
#if defined(MULTIPLE_SCATTERING)
    // Multiple scattering image-based lighting
    // Slightly energy loss in lambertian diffuse part.
    if (weighty >= 0.5f)
    {
#if 1   
        vec3 Fr = max(vec3(1.0f - mi.perceptualRoughness), mi.specular) - mi.specular;
        vec3 kS = mi.specular + Fr * pow(1.0f - si.NoV, 5.0f); 
        vec3 FssEss = kS * dfg.xxx + dfg.yyy;
        float Ess = dfg.x + dfg.y;
        float Ems = 1.0f - Ess;
        vec3 Favg = mi.specular + (1.0f - mi.specular) / 21.0f;

        // Not so sure...
        vec3 Fms = FssEss * Favg / (1.0f - (1.0f - Ess) * Favg);
        // vec3 Fms = FssEss * Favg * mi.specular / (1.0f - (1.0f - Ess) * Favg * mi.specular);

        vec3 Edss = 1.0f - (FssEss + Fms * Ems);
        vec3 kD = mi.diffuse * Edss;

        envFd = (Fms * Ems + kD) * envDiffuse; 
        envFr = FssEss * envSpecular;
#else
        // From unreal engine (if I made it right)
        vec3 FssEss = mi.specular * dfg.xxx + dfg.yyy;
        float Ess = dfg.x + dfg.y;
        vec3 energyCompensation = 1.0f + mi.specular * (1.0f / Ess - 1.0f);
        float energyConservation = 1.0f - dot(vec3(0.299f, 0.587f, 0.114f), FssEss * energyCompensation);
        envFd = mi.diffuse * energyConservation * envDiffuse;
        envFr = FssEss * energyCompensation * envSpecular;
#endif
    }
    else
    {
        vec3 FssEss = mi.specular * dfg.xxx + dfg.yyy;
        envFd = mi.diffuse * envDiffuse * (1.0f - FssEss);
        envFr = FssEss * envSpecular;
    }
#else
    vec3 FssEss = mi.specular * dfg.xxx + dfg.yyy;
    envFd = mi.diffuse * envDiffuse * (1.0f - FssEss);
    envFr = FssEss * envSpecular;
#endif

    return envFd + envFr;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 ta = vec3(0.0f);
    float phi = PI;
    float theta = 0.5f * PI;
    if (iMouse.z > 0.0f)
    {
        phi = 2.0f * PI * (iMouse.x / iResolution.x);
        theta = PI * (iMouse.y / iResolution.y);
    }

    vec3 ro = 3.0f * vec3(cos(phi) * sin(theta), cos(theta), sin(phi) * sin(theta));
    mat3 ca = setCamera(ro, ta, 0.0f);

    vec2 p = (2.0f * fragCoord - vec2(1.0f, 1.0f) * iResolution.xy) / iResolution.y;
    float fl = 2.5f;
    vec3 rd = ca * normalize(vec3(p, fl));
    vec3 col = render(ro, rd);

    fragColor = vec4(col, 1.0f);
}
