void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    if(uv.y > 0.66) fragColor = vec4(0.9, 1.0, 0.0, 0.0);

    else if(uv.y < 0.33) fragColor = vec4(0.8, 0.0, 0.0, 1.0);

    else fragColor = vec4(0.0, 0.7, 0.3, 0.0);

}
