vec3 rostroGoku(vec2 uv, vec3 col)
{  vec2 pos=vec2(-4.0,-2.);
    col= orejaIzq(uv,col);
    col= orejaIzq(vec2(-uv.x,uv.y)-vec2(-0.73,0.05),col);
    col= cabeza(uv-pos,col);
    col=cejader(uv-vec2(0.0,0.0), col);
    col=cejaIzq(uv-vec2(0.0,0.0), col);
    col= ojogokuDer(uv-pos, col);
    col= ojogokuIzq(uv-pos, col);
    col= bocaci(uv, col); 
return col;
}

vec3 fondo(vec2 uv)
{
   vec2 uv1=floor(uv*32.0+sin(uv));
    vec2 uv2=floor(uv*16.0*cos(uv))*sin(uv);
    float t=0.54;
    float t2=iTime;
    float t1=t2*2.5;
    float f=sin(t*2.0);    
    vec3 col1 = 0.5 + 1.5*cos(t1+uv1.xxx+vec3(1,2,0.4)*uv2.xyx);
    vec3 col2 = 0.5 + 0.5*cos(t1+uv2.yxx+vec3(2,2.2,0.2));
    vec3 col=mix(col1, col2, f);
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{ vec2 uv = -1.0 + 2.0 * fragCoord.xy/iResolution.xy;
    uv.x *= iResolution.x/iResolution.y;
    uv-=vec2(0.0,-0.5);
    vec2 uv0=uv;
    uv*=4.0;
    vec3 col=fondo(uv0/2.0);
    float t=iTime;
    uv*=abs(1.5*sin(t));
    float s=sin(t);
    float c=cos(t);
    col= rostroGoku((uv-vec2(-1.0*c,-3.0*s))*0.25,col);
    col= rostroGoku(vec2(uv.x,uv.y)*0.5,col);
    col= rostroGoku(vec2(uv.x,uv.y)*0.5,col);
    col= rostroGoku(uv-vec2(1.0*s,1.0*c),col);
fragColor = vec4(col,1.0);
}
