precision highp float;

float mandelbrot(vec2 coord, float N){
    vec2 z = coord;
    float i = 0.0;
    while(i<N){
        z = vec2(z.x*z.x - z.y*z.y, 2.0*z.x*z.y) + coord;
        if(length(z) > 2.0){
            return i/N;
        }
        i += 1.0;
    }
    return 1.0;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    float ts = mod(iTime*0.2,18.5);
    vec2 uv = vec2(3.0*fragCoord.x/iResolution.y - 3.5, 3.0*fragCoord.y/iResolution.y - 1.5);
    uv *= 1.0/pow(2.0,ts);
    uv.x += 0.338962;
    uv.y += 0.069877;
    float pv = mandelbrot(uv,ts*30.0 + 50.0);
    vec3 col = vec3(0.0);
    if(pv > 0.1){
        if(pv < 0.9){
            if(pv > 0.5){
                col = vec3(pv,length(uv)*1.1,length(uv)*3.0-pv*1.2);
                }
            else{
                col = vec3(length(uv)*0.6, pv*0.7, 0.2);
            }
        }
        else{
            col = vec3(pv)*0.8;
        }
    }
    fragColor = vec4(col,1.0);
}
