vec3 palette( in float t) {
    vec3 a = vec3(0.382, 0.527, 0.198);
    vec3 b = vec3(0.610, 0.807, 0.044);
    vec3 c = vec3(0.977, 1.167, 1.448);
    vec3 d = vec3(3.979, 5.652, 3.138);
    return a + b * cos( 6.28318 * (c * t + d));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord.xy / iResolution.xy;
    st -= .5;
    st.x *= iResolution.x / iResolution.y;
    vec3 finalColor = vec3(0.0);
    float y = st.y;
    float x = st.x;
    float ripples = sin((x * 10. - y * 30.) * 3.) * .5 + .5;
    float waves = sin((x * 2. - y * 6. + iTime * .2));

    float bands = sin(y * 10. + x * 30.);
    float sharpenBands = smoothstep(-.2, .2, bands);
    float band2 = smoothstep(mix(.2, 20., sin(iTime) * .5 + .5), .2, bands - 0.5);

    float m = sharpenBands * (1. - band2);
    m = max(m, ripples * band2 * max(0., waves));
    m += max(0., waves * 0.8 * band2);

    vec3 color = vec3(0.0);
    float d = 0.0;
    float val = sin(iTime * .2) * 2. + 29.;

    d = length(sin(min(abs(st)-.1,0.) + val * .10) );
    d /= length(sin(max(abs(st)-.1,0.) + val * .10) );

    color = palette(length(d) + .001 + iTime * 0.15);
    color -= d;


    d = sin(y * 1.0 + iTime) / 1.0;
    d = abs(y);     
    y = pow(0.01 / x, 1.2);

    color += mix(d, 1. - m, smoothstep(-.3, .3, sin(x * 2. + iTime)));

    finalColor -= color;

    fragColor = vec4(vec3(finalColor),1.0);
}
