#define pi 3.1415926

mat3 yaw_pitch_roll(float yaw, float pitch, float roll)
{
    mat3 R = mat3(vec3(cos(yaw), sin(yaw), 0.0), vec3(-sin(yaw), cos(yaw), 0.0), vec3(0.0, 0.0, 1.0));
    mat3 S = mat3(vec3(1.0, 0.0, 0.0), vec3(0.0, cos(pitch), sin(pitch)), vec3(0.0, -sin(pitch), cos(pitch)));
    mat3 T = mat3(vec3(cos(roll), 0.0, sin(roll)), vec3(0.0, 1.0, 0.0), vec3(-sin(roll), 0.0, cos(roll)));

    return R * S * T;
}

#define EPSILON 0.001
#define MAX_DIST 50.0
#define MAX_ITER 200u

// adapted from https://www.shadertoy.com/view/3syGzz Limited Repetition SDF by iq
float repeat(float p, float s, float h)
{
    return p-s*clamp(round(p/s),-h/s,+h/s);
}

// based on https://iquilezles.org/articles/distfunctions/
float line( vec3 p, vec3 a, vec3 b )
{
    vec3 pa = p - a, ba = b - a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}

float cylinder(vec3 p, float r, float h)
{
    float d = length(p.xy)-r;
    // extrusion https://iquilezles.org/articles/distfunctions/
    vec2 w = vec2( d, abs(p.z) - h);
    return min(max(w.x,w.y),0.0) + length(max(w,0.0));
}

float halfspace(vec3 p)
{
    return p.z;
}

vec3 eightfold(vec3 p) 
{//return p;
    p.xy = abs(p.xy); // quadrants
    return p.x<p.y?p.yxz:p.xyz; // diagonal
}

float octant_horizontal(vec3 p)
{
    float s = sqrt(3.0);
    float lines = line(p,vec3(s/2.0,0.5,0),vec3(1,1,0));
    lines = min(lines, line(p,vec3(s/2.0,0.5,0),vec3(1,0,0)));
    lines = min(lines, line(p,vec3(s/2.0,0.5,0),vec3(vec2(1.0+s)/4.0,0)));
    lines = min(lines, line(p,vec3(1,0,0),vec3(1,1,0)));
    lines = min(lines, line(p,vec3(s/2.0,0.5,0), vec3(1,0.5,0)));
    return lines;
}

float octant_vertical(vec3 p)
{
    float h = 1.0;
    float s = sqrt(3.0);
    float d =
        min
        (
            min
            (
                length(p.xy-vec2(s/2.0,0.5)),
                length(p.xy-vec2(1,0.5))
            ),
            min
            (
                length(p.xy-vec2(1)),
                length(p.xy-vec2(1,0))
            )
        );

    // extrusion https://iquilezles.org/articles/distfunctions/
    vec2 w = vec2( d, abs(p.z) - h);
    return min(max(w.x,w.y),0.0) + length(max(w,0.0));
}

float octant_diagonal(vec3 p)
{
    float s = sqrt(3.0);
    float lines = line(p,vec3(s/2.0,0.5,0),vec3(1,1,1));
    return lines;
}

float dodecagonal_transition_structure(vec3 p)
{   
    vec3 q = eightfold(p);
    return min(octant_horizontal(vec3(q.xy,repeat(q.z, 0.5, 1.0))), octant_vertical(q));
}

float map(vec3 p)
{
    // https://www.wolframalpha.com/input?i=regular+dodecagon+diameter+2+incircle
    float r = -(-1.0 + sqrt(3.0))/(2.0*sqrt(2.0)*(-2.0+sqrt(3.0)));

    return min(dodecagonal_transition_structure(p)-0.01, cylinder(p, r, 1.0));
}

// https://iquilezles.org/articles/normalsSDF tetrahedron normals
vec3 normal( vec3 p ) 
{
    const float h = EPSILON;
    const vec2 k = vec2(1,-1);
    return normalize( k.xyy*map(p + k.xyy*h) + 
                      k.yyx*map(p + k.yyx*h) + 
                      k.yxy*map(p + k.yxy*h) + 
                      k.xxx*map(p + k.xxx*h) );
}

float trace(vec3 ro, vec3 rd, float t0, float t1)
{
    uint i;
    float t;
    for(t = t0, i = 0u; t < t1 && i < MAX_ITER; i++)
    {
        float h = map(ro + rd * t);
        if(h < EPSILON)
            return t;
        t += h;
    }
    return MAX_DIST;
}

// https://iquilezles.org/articles/rmshadows
float shadow(vec3 ro, vec3 rd, float t0, float t1)
{
    return trace(ro, rd, t0, t1) < t1 ? 0.0 : 1.0;
}

// https://iquilezles.org/articles/rmshadows
float softshadow( in vec3 ro, in vec3 rd, float mint, float maxt, float k )
{
    uint i;
    float res = 1.0;
    float ph = 1e20;

    float t;
    for(t=mint, i = 0u; t<maxt && i < MAX_ITER; i++)
    {
        float h = map(ro + rd*t);
        if( h < EPSILON )
            return 0.0;
        float y = h*h/(2.0*ph);
        float d = sqrt(h*h-y*y);
        res = min( res, k*d/max(0.0,t-y) );
        ph = h;
        t += h;
    }
    return res;
}

// https://www.shadertoy.com/view/Xds3zN raymarching primitives
float calcAO( in vec3 pos, in vec3 nor )
{
	float occ = 0.0;
    float sca = 1.0;
    for( int i=0; i<5; i++ )
    {
        float h = 0.01 + 0.12*float(i)/4.0;
        float d = map(pos + h*nor);
        occ += (h-d)*sca;
        sca *= 0.95;
        if( occ>0.35 ) break;
    }
    return clamp( 1.0 - 3.0*occ, 0.0, 1.0 ) ;
}

void mainImage( out vec4 fragColor, in vec2 I )
{
    bool demo = all(lessThan(iMouse.xy, vec2(10.0)));
    vec2 R = iResolution.xy;
    //I = 2.0 * I.xy / R - 1.0;
    //I.x *= R.x / R.y;
    I = (2.0 * I - R) / R.y; // concise scaling thanks to Fabrice
    float yaw = 2.0 * pi * float(iMouse.x) / float(R.x);
    float pitch = pi - pi / 2.0 * float(iMouse.y) / float(R.y);
    yaw = !demo ? yaw : 2.0 * pi * fract(iTime * 0.01);
    pitch = !demo ? pitch : 1.25 * pi / 2.0;

    vec3 ro = vec3(0.0, 0.0, -5.0);
    vec3 rd = normalize(vec3(I.xy, 2.0)); // NOTE: omitting normalization results in clipped edges artifact

    mat3 M = yaw_pitch_roll(yaw, pitch, 0.0);
    ro = M * ro;
    rd = M * rd;

    ro.z += 0.0;

    float dist = trace(ro, rd, 0.0, MAX_DIST);
    vec3 dst = ro + rd * dist;
    vec3 n = normal(dst);

    vec3 lightdir = normalize(vec3(cos(iTime/5.), sin(iTime/5.), 1.0));
    vec3 ambient = vec3(0.1);
    float brightness = max(dot(lightdir, n), 0.0);
    if(brightness > 0.0) brightness *= shadow(dst + n * 0.01,lightdir, 0.0, MAX_DIST);
    //if(brightness > 0.0) brightness *= softshadow(dst + n * 0.01,lightdir, 0.0, MAX_DIST, 100. );
    vec3 color = vec3(1);
    color = (n * 0.5 + 0.5);
    color = (ambient * calcAO(dst, n) + brightness) * color;
    //color = (ambient + calcAO(dst, n) * brightness) * color;

    //color = mix(color, vec3(0.0), step(MAX_DIST, dist));
    color = mix(color, vec3(0.0), smoothstep(MAX_DIST-1.0, MAX_DIST, dist));

    fragColor = vec4(sqrt(color), 1.0); // approximate gamma
}
