#define R(p,a,r)mix(a*dot(p,a),p,cos(r))+sin(r)*cross(p,a)
#define H(h)(cos((h)*6.3+vec3(0,23,21))*.5+.5)
void mainImage(out vec4 O, vec2 C)
{
    O=vec4(0);                                 // change parts 
    vec3 p,r=iResolution,
    d=normalize(vec3((C-.5*r.xy)/r.y,1.*0.6));  // 1.*0.6
    for(
        float i=0.,g=0.,e,s;
        ++i<99.;
        O.rgb+=mix(r/r,H(log(s)),.7)*.08*exp(-i*i*e))
    {
        p=g*d;
        p.z-=.6;
        p=R(p,normalize(vec3(1,2,1)),iTime*.13);  // iTime*0.13
        s=4.;
        for(int j=0;j++<8;)
            p=abs(p), //p=p.x<p.y?p.zxy:p.zyx,  // delete
            s*=e=1.8/min(dot(p,p),1.3*1.105),   // 1.3*1.105
            p=p*e-vec3(12,3,3)*1.5;           // *1.5
        g+=e=length(p.xz-0.5)/s;             //p.xz-0.5

    }
    O=pow(O,vec4(5));
 }
