// thanks to FabriceNeyret2 for helping clean this up!

#define S(m) ( 1. + sin( m * length( fract(U+U) - .5 ) + iTime*8.) )

void mainImage( out vec4 O, vec2 u )
{
  vec2  R = iResolution.xy, 
        U = (u+u - R ) / R.y;

  O.rgb = S(60.)*S(80.)*S(100.) /4.
          * (   vec3(.82, .33, .24)            // palette(t)
              + vec3(.66, .48, .9)
              * cos( 6.28*(    vec3(.61,.34,.3) 
                            * ( length(U) + .3*iTime )
                            +  vec3(2.82,3.03,-.27) ) ) 
            );
}
