vec2 hash(vec2 p)
{
    p*=mat2(788.,721.,732.,841.);
    return 2.*fract(sin(p)*56921.693)-1.;
}
float noise(vec2 p)
{
    vec2 i=floor(p);
    vec2 f=p-i;
    vec2 u=f*f*f*(6.*f*f-15.*f+10.);
    return mix(mix(dot(hash(i+vec2(0.,0.)),f-vec2(0.,0.)),dot(hash(i+vec2(1.,0.)),f-vec2(1.,0.)),u.x),
                mix(dot(hash(i+vec2(0.,1.)),f-vec2(0.,1.)),dot(hash(i+vec2(1.,1.)),f-vec2(1.,1.)),u.x),u.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 p=uv;
    p.x-=noise(vec2(iTime,uv.y*0.3))*0.5;
    p.x-=noise(vec2(iTime*4.,uv.y*2.3))*0.1;
    //p.y-=smoothstep(0.1,0.9,sin(iTime*10.)*10.)*0.3;
    // Time varying pixel color
    vec3 col = texture(iChannel0,p).rgb;
    col=mix(col,vec3(hash(vec2(p.y*iTime)).x),0.1);
    col.r=mix(col.r,texture(iChannel0,p).g,0.25);
    col.g=mix(col.g,texture(iChannel0,p).b,0.25);
    col.b=mix(col.b,texture(iChannel0,p).r,0.25);
    // Output to screen
    fragColor = vec4(col,1.0);
}
