#define T(x) texture(iChannel0, fract((x)/iResolution.xy))

void mainImage(out vec4 c, vec2 u)
{   
    //c=1./u.yyyx;
    c=u.yyyx/1e4;///iTime;
    //for(float t=1.4; t<1e2; t+=t)
    //    c += (c.gbar-c)/3.+T(u-c.wz*t);
    vec4 offs = vec4(
        2.00,
        1.3,
        0.80,
        0.25
    );

    for(float t=.6; t<4e2; t+=t*1.123)
    	c += c.gbar/3.-c/3.0*offs+T(u-c.ab*t);

	c = mix(T(u+c.xy), cos(1.20*c), .07);
}
