void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    fragCoord *= 2.0;
    fragCoord.x -= fragCoord.y * 0.5;

    if((int(fragCoord.x) & int(fragCoord.y)) == 0) {
        fragColor = vec4(1.0, 1.0, 1.0, 1.0);
    } else {
        fragColor = vec4(0.0, 0.0, 0.0, 0.0);
    }

    fragCoord.x += fragCoord.y * 0.5;

    if(int(fragCoord.x) > 2048 || int(fragCoord.y) > 2048) {
        fragColor = vec4(0.0, 0.0, 0.0, 0.0);
    }
}
